CREATE TABLE IF NOT EXISTS `gonsieven` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `content` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

--
-- 转存表中的数据 `gonsieven`
--

INSERT INTO `gonsieven` (`id`, `content`) VALUES
(1, '<div>\r\n</div>\r\n<p style="text-indent:2em;">\r\n	<br />\r\n</p>\r\n<div>\r\n	<table border="0" class="ke-zeroborder">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">企业大事记</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2010年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月公司建立知识产权试点工作领导小组，进一步推进和完善公司知识产权</span><span style="font-family:NSimSun;font-size:14px;line-height:2;">管理工作。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月&nbsp;公司成立了硬质合金技术委员会，对公司硬质合金领域内的重大工艺变革、技术研发方向、技改项目等进行技术论证，有利于避免公司的技术决策失误，加快了硬质合金生产的技术进步步伐。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月公司成立了投资项目专家评审委员会，为进一步做好投资项目实施前的专业评审、加强对投资项目的必要性和可靠性把握、提高投资项目对公司当前和长远发展的支撑有积极的作用。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月钨钼制品搬迁项目完成并开始恢复生产。钨钼制品（502）搬迁工程是成都钨钼加工基地成建制整体搬迁的一个重点项目。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月由自硬公司与瑞士HF Technologies AG公司共同投资1.6亿元新建而成的自贡长城硬面材料有限公司开业庆典在板仓工业园举行。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月公司决定于5至12月开展"降成本、增效益"主题劳动竞赛，动员和组织职工全力以赴完成各项工作任务，确保公司生产经营目标的实现。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月　公司党委正式启动以"做优做强科学发展，敬业建功争当先锋"为主题的创先争优活动。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月四川省委书记、省人大常委会主任刘奇葆和省委常委、省国资委党委书记王少雄一行来到板仓工业园区长城硬面材料有限公司视察。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月&nbsp;在混合料分厂、设备基建部、计控中心及施工单位的通力合作下，为期一个半月的"己烷加量系统技术改造"项目通过正式验收。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 矿用合金新生产线压制工序正式投入生产，宣告公司2010年最后一个技改项目基本完工。这一年，公司重点推进硬质合金生产系统的技改提升、钨钼制品的调整发展，着力优化产业结构、提升产品品质、加快建设三大基地。全年实际投资1.4亿元，完成了矿用合金新生产线建设搬迁、刀片技术改造、钼制品生产线整体搬迁，启动了模体模具与精密耐磨零件的分线生产、粉末技术改造等项目，大力推进了采掘工具技改项目。大异型棒材、硬面深加工、细钨丝、螺纹刀具方面加强了滚动投入，热喷涂应用和采掘工具项目展开了前期准备工作，蓄势加快发展的格局基本形成。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司举行了质量环境安全管理体系与五矿集团HSE体系对接启动运行仪式。实现公司HSE体系与集团公司安全生产及节能减排管控模式的对接，有利于适应五矿集团多行业、多体制、多层次的安全环保监管模式，使公司安全、环保、节能减排处于受控状态。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司操作族职业技能鉴定的操作技能考试顺利结束。考试从9月9日开始，历时两个多月，先理论考试后操作技能考试，来自九个单位的347名操作族员工参加此次鉴定，最终196人通过了鉴定考试。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司职工关爱互助基金会启动动员大会在综合部多功能厅召开。职工关爱互助基金会拟从公司、工会的拨款及职工入会基金等三个方面筹集资金，以进一步建立起对特殊困难职工的长效帮扶机制，更好地为职工群众服好务。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司羽毛球代表队在全国有色金属行业羽毛球团体比赛中，荣获团体第三名，这是公司成立以来第一次参加全国性体育比赛，也是公司有史以来取得的最好比赛成绩。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司召开安全供电一万天庆祝大会。27年来，公司高度重视安全生产，始终把"安全第一，预防为主"的工作方针放在首位，实现了连续安全供电10000天，累计供电量12.23亿度。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 2010年，公司全体干部职工积极落实"保增长、调结构、增效益"的总体部署，抓住了市场需求回升的机遇，生产经营持续恢复性增长，整体盈利能力明显改善，主要经济指标全面达到目标要求，生产经营取得好的效果，实现了全面扭亏增盈。子公司业绩持续向好，成为利润重要来源。公司全年实现净利润1800万元；实现销售收入15.6亿元，完成率为107%；完成销量5971吨，完成率为107%。2010年成为公司改制以来指标完成最好的一年。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2009年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 我公司研发中心项目顺利通过由省商务厅、财政厅组织召开自贡市科技兴贸创新基地公共服务平台项目验收会的验收。研发中心公共服务平台项目于2007年开始分步启动实施，2009年9月该项目全面建成并投入使用。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司迎新年职工文艺汇演在市川剧院举行，16个基层工会和联队自编自演了舞蹈、歌舞和语言三大类16个文艺节目。此次演出既是公司职工展示才华、汇报成果的机会，也是对各单位凝聚力和精神风貌的一次检阅。整个演出始终洋溢着积极向上、团结奋进的良好氛围。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 1至12月，公司完成硬质合金产品综合合格率97.65%，硬质合金产品送检一次合格率96.7%，硬质合金产品退赔换率0.29%，冶炼、粉末产品退赔换率完成0.03%，全面达到公司年初制定的质量目标，除硬质合金产品送检一次合格率外，其余指标还创下了近三年来的新高。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 经公司研究，决定撤销北京、上海、沈阳、西安等4个销售分公司。撤销后，将在上述四个地区设立相应商务办，承担市场开发、市场维护、客户服务职能；在上海、沈阳设立中心仓库，承担公司产品物流配送职责。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司实验室通过CNAS现场复评审核。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 截止到11月末，粉末分厂共完成产量7845吨，为年度目标任务的105%，提前一个月完成年度目标，预计全年粉末制品产量8200吨。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 1至10月粉末分厂外销粉末制品1920吨，为全年计划的111.63%，超全年奋斗目标200吨。其中完成AMT销售687吨，超全年奋斗目标500吨；完成超细WC销售23.56吨，超全年外销奋斗目标13吨。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司召开学习实践科学发展观活动总结大会。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 新改版的公司网站正式上线运行。网站采用全新的设计技术，突出公司产品介绍，强化对客户服务，注重企业运营成效和员工风貌的宣传。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司八月份生产经营传来捷报，产量、销量、货款回笼、利润等主要指标均创下了今年以来的月度新高，剔除浮亏因素，当月实现利润800万元，扭转了金融危机以来产销不旺的被动局面。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 销售公司邀请自贡市釜溪职业学校的专职礼仪教师为28名销售人员进行了“商务礼仪知识”培训。本次培训将对提高业务人员营销能力起到一定的促进作用，为客户经理完成09年的销售目标起到推动作用。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 为践行科学发展观，体现共产党员先进性，迎接中国共产党建党88周年的到来，公司组织各单位部室100多名党员，在公司厂区绿化带开展了一次“清除杂草、美化环境”的党员义务劳动。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 成都分公司拓市场、保目标，上半年产品销售形势喜人，累计完成产品销量407吨，其中，钼丝产品内销87吨，同比增长27%，为年计划的60%。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 为倡导健康生活，构筑和谐自硬，由公司工会主办的职工大众体育运动会在俱乐部灯光球场拉开序幕。来自23个基层分会的400多名运动员组队参加了比赛。比赛有跳长绳、时代列车、力士竞速、醉书接力、拔河等5个趣味项目。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 我公司和瑞士HF Technologies AG投资设立的“自贡长城硬面材料有限公司”工商注册成立。硬面合资公司的正式成立，为我公司打造具有世界水平的硬面材料生产研发基地打下基础。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 由17名模范班组长和14名先进职工组成的疗休队伍赴贵州开始了为期7天的疗休。公司在生产经营还很困难的时候，继续坚持疗休制度，体现了公司“以人为本”的经营理念和全心全意依靠职工办企业的指导思想。 </span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自贡市庆五·一“工人先锋号”命名表彰暨第七届职工技能大赛启动仪式在自贡板仓高新创业孵化园隆重举行。刀片分厂特种工段压制组被授予四川省“工人先锋号”，耐磨零件分厂深加工段检查组被授予自贡市“工人先锋号”光荣称号。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司召开了质量工作会，会议对今年的质量改进工作做了进一步的部署和安排。此次会议的召开对公司全年产品质量的迅速提升起到关键作用。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 成都分公司到沈飞公司上门服务，召开刀具产品使用技术交流活动。通过沟通与交流，沈飞公司对我公司“长城牌”刀具产品有了更全面和深入的认识，为我公司争取更多订单打下了较好的基础。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△2月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 由市场部、技术部汇同自贡市工商局、成都市工商行政执法大队组成的联合打假组，对成都地区的制假贩假活动开展了一次规模较大的专项打假整治行动。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 成都分公司设立钨钼销售部，具体负责钨钼、钼丝和钨丝等产品的集中销售。此次调整对进一步提高工作效率和市场应变能力，实现销售规模的迅速扩大有着积极作用。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 经公司研究，决定撤（并）成都销售分公司、广州销售子公司、济南销售分公司、哈尔滨销售分公司等四个销售网点，在撤（并）的同时，在四个城市设立相应的商务办。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司对部分销售人员进行了调整，有11人调到刀片分厂负责特种刀片的市场开发和产品销售，有2人调到硬面材料有限公司负责硬面材料产品的市场开发和产品销售。此次人员调整，为公司实施产销一体化经营管理试点工作拉开了序幕。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2008年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自贡市召开第六届职工技能大赛表彰暨工会经济技术工作会。我公司缪洪林、曾涛、范卫斌、陈超4名获得技能大赛比赛项目第一名的选手被市委组织部、市劳动和社会保障局、市总工会联合授予“2008年自贡市技术能手”荣誉称号；我公司职工李兴明、陈超、张勇提出的3项合理化建议及技术改进项目分别被评为自贡市第四届职工合理化建议及技术改进成果一、二、三等奖。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自贡长城硬面材料有限公司召开了创立大会股东会议，会议审议通过了《公司章程》，完成了董事会、经理层、监事会的机构设置。2009年新公司将开始独立运行。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 成都公司钨丝分厂生产线实现全面拉通，标志着钨丝技改项目全面完成。钨产品将成为成都公司新的经济增长点。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 硬面材料项目在自贡市板仓工业园区启动建设。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 成都钨钼加工基地动力能源配套项目全面竣工。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 四川省委书记刘奇葆一行在市领导王海林的陪同下，到公司视察。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 5月12日下午14:28分，四川汶川发生.8级地震，自贡地区有强烈震感，地震发生后，公司积极部署抗震救灾工作，确保了生产经营秩序和人身与公司财产的安全。同时公司先后派遣13名员工支援灾区一线公司，向灾区人民捐款120万元，支援2辆货车用于运送物资。全公司干部职工捐款31.8万元，党员交纳“特殊党费”7.08万元，团员交纳“特殊团费”4000余元。公司获四川省红十字会授予的抗震救灾特别贡献奖。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 由我公司承担的“硬质合金深井潜油泵零件开发”等五个科技成果通过自贡市科技局组织的专家技术鉴定。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司获全国总工会授予的“全国五一劳动奖状”荣誉称号。公司党委书记孙从学代表公司进京领奖，受到王兆国等党和国家领导人的亲切接见。这是公司第二次获得此殊荣。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司与自贡科瑞德新材料有限责任公司部分自然人股东签署《股权转让协议》，收购58名自然人股东持有的股权，控制权达到94%，绝对控股科瑞德公司。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司工会获2007年度自贡市工会工作目标考核特等奖，质检中心刀片检查一组工会小组获四川省模范职工小家称号。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2006年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 12月11～12日公司一届董事会第二次会议在公司招待所大会议室召开。会议审议了《2006年总经理工作报告》、《自硬公司“十一五”发展规划》等十一个议案。明确了“十一五”发展方向和未来五年的项目投资计划。“十一五”期间，自硬公司将在湖南有色和中钨高新总体战略指导下，全面贯彻科学发展观，坚持“市场导向，优势竞争”的经营理念，以结构调整为主线，以改革创新为动力，以技术进步为支撑，抓住机遇，加快发展；规划投资10亿元，发展三大业务板块，将公司建成“国内主要的硬质合金生产基地、国内重要的钨钼制品生产基地、具有世界水平的硬面材料生产基地”，打造国内一流、国际有影响力的强势企业。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月15日公司将冶炼分厂60米高烟囱进行拆除。至此，这座见证了自硬公司38年发展壮大历史的地标性建筑完成了它的历史使命，为冶炼分厂钨冶炼项目的顺利实施拉开了序幕。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月公司质检中心实验室成功申请获准使用“中国合格评定国家认可委员会”（LAC-MRA/CNAS）和国际实验室认可合作组织--多边互认协议（ILAC-MRA）的联合标识。?</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 10月16日公司注册使用的“长城”牌商标被国家工商总局评定为“中国驰名商标”，这是自贡市首家也是国内硬质合金行业第二家获此殊荣的企业。12月29日，四川省人民政府在成都市锦江宾馆召开了荣获中国弛名商标企业表彰大会，对我公司给予了10万元奖励。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 10月20日自硬公司第六次党员代表大会（暨新公司成立以来的首次党代会）隆重召开。选举产生了公司第六届党委委员和纪委委员。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 9月4日管理部室岗位竞聘工作拉开了序幕，这是公司自1998年精简机构和人员以来最大的一次举措。此次竞聘岗位涉及调整的管理部室等单位的全部非领导职位，供选职位数共607个，报名的员工有642人，其中139人报了两个职位。整个竞聘工作于9月10日前全部完成，9月20日前基本完成了精简人员分流与安置工作。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 8月29日公司召开机构调整及人员精简工作布署会。同时颁文对组织机构、部份职能及中层干部进行了调整。此次组织机构的调整，重点在管理部门。经过调整，公司管理部门由过去的21个减少到13个，精简率为35%；中层干部由83名减少到77名。此次调整涉及面较大，共调整了42名中层干部，其中免去13名同志的领导职务，新提拔36岁以下的7名年轻干部，中干队伍平均年龄下降2～3岁。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 8月份公司提出开展“拓市场、降成本”主题劳动竞赛活动，突出市场开拓、降低成本、节约费用等重点，确定了35个公司重点项目，下发了《关于确定“拓市场、降成本”主题劳动竞赛重点项目的通知》，制定专项奖励办法，激发了干部职工积极参与活动、全力以赴完成公司年度目标任务的激情和斗志，为公司增收节支约2400万元。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 7月16日新公司召开了第一次股东会，推选产生了新一届董事会、监事会，并由董事会聘任组成了新一届经理班子。7月19日，湖南有色自贡硬质合金有限责任公司在德胜广场举行成立揭牌仪式。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 6月21日公司在俱乐部大礼堂召开了二届九次职工代表大会，讨论审议并通过了《产权制度改革方案》和《职工安置方案》。当日下午五点多，市国资委与湖南有色正式签订了《增资扩股协议书》，成功引进湖南有色对自硬公司增资扩股4亿元。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 5月22日副省长杨志文在市领导刘捷、王海林、刘国庆以及市国资委等部门负责人陪同下来公司调研、指导工作，并在公司招待所召开了“工业强省工作自贡市企业座谈会”。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 4月21日公司决定成立治理商业贿赂专项工作领导小组。治理商业贿赂专项工作分五个阶段进行，我公司领导首次与国资委签订了书面廉洁承诺书。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 3月7日市长王海林、市人大常委会副主任吴长生率队就新材料产业集群情况到我公司调研指导。公司董事长张毅就《“工业强市”战略背景下自硬公司的现状及发展》做了汇报。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 3月份PM管理活动于三月份正式在各相关分厂实施。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 1月1日硬质合金刀片实现按片计价，提升了刀片的利润率。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2005年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 今年，受钨资源价格暴涨和市场需求的影响，公司生产出现大幅下滑。面对这一艰难局面，公司上下团结一心，多管齐下，想办法解决资金困难，缓解生产经营压力，通过艰苦努力，圆满地完成了今年的生产经营目标。全年完成产品产量5171吨，为年度目标的107.6%；实现工业产值13.2亿元，同比增长15.09%；销售收入14.06亿元，同比增长20.87%；其中出口创汇7213万美元，同比增长26.46%；实现利润3480万元，同比增长47.08%，均创历史新高。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月7日，继2001年公司通过马德里国际注册和单个国家注册方式分别在美国、英国等11个国家成功注册后，今年，公司“长城CHANGCHENG及图”商标通过委托方式又分别在尼泊尔、加拿大、泰国、阿联酋、老挝、巴西、香港、南非、孟加拉、马来西亚、阿富汗、以色列、菲律宾进行国际注册；目前这十三个国家的查询手续已经完成，根据各国建议，可以进入申请注册，若在无异议情况下，申请注册有望顺利进行。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月18日和21日，公司分别在市川剧院和公司灯光球场隆重举行了建厂四十周年庆典。公司邀请了中国有色金属协会主席康义、副主席周菊秋、省人大副主任刘永顺、省总工会副主席罗林书、市委书记唐坚、市委副书记黄一、副市长刘国庆、中钨高新公司名誉董事长叶选基，以及省市各级机关、各兄弟单位领导及客户单位等共100多位来宾参加了18日的庆典活动。11月21日，四千多名职工参加了厂庆联欢晚会，饱含深情载歌载舞，祝福公司四十华诞。公司还举行了新陈列馆开馆仪式，召开了劳模先进和老同志座谈会，举办了职工书法和美术摄影作品展以及大型职工签名活动。这些文化气息浓郁的活动，充分展示了公司企业文化的深厚底蕴和独特的魅力，展示了自硬人的风采，宣扬了“志存高远，自强不息，团结务实，开拓创新”的自硬精神。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月18日，为了给公司四十周年庆典献上一份贺礼，更为了向广大客户和各界友人全面展示公司的发展历程、辉煌成就以及各种产品，公司陈列馆在中国有色金属协会主席康义、中国钨业协会会长周菊秋的揭牌下，隆重开馆。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 10月25日，四川省质量技术监督局受国家中启计量体系认证中心委托组成评审组对公司测量管理体系进行了现场确认评审，并予以通过。这样，我公司不仅成功地进行了体系的转版，并且成为了国家质检总局和国家认监委联合发布《测量管理体系认证管理办法》后的四川省第一家被认证的单位，也是四川省2005年唯一获得新组建的国家测量管理体系认证机构—国家中启计量体系认证中心颁发新的认证证书的单位，同时还是全国首批取得“AAA”级证书的第十家单位。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司在省企业质量信誉等级评定中，被省质量技术监督局评为AAA级企业。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司继续被四川省出入境检验检疫局确定为“一类出口工业产品生产企业”,有效期为两年。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 8月5日，公司总经理彭绍雨带领公司发展部、财务部、人力资源部部长、信息部、总经办等部门负责人，专程到成都分公司就上半年生产经营情况与成都分公司全体中层以上干部进行沟通和交流。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 7月29日，公司在俱乐部礼堂隆重召开了保持共产党员先进性教育活动动员大会，标志着第二批保持共产党员先进性教育活动在我公司正式启动。为搞好此次活动，公司党委专门成立了“保持共产党员先进性教育活动领导小组”，制定了具体实施方案。整个教育活动于12月23日圆满结束。此次活动的开展强化了党员的先锋意识，树立了党员的良好形象，促进了公司各项工作的稳步发展，增强了党组织的创造力、凝聚力和战斗力。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 6月7日，公司召开“让每一份资源发挥最大效益”主题活动动员大会。该活动以激发广大职工“点滴节约，当家理财”的主人翁意识和责任感为目标，各单位制定活动规划共193项，通过查找生产经营中的浪费和漏洞，盘活现有的和潜在的资源，节约或创新价值达2000多万元。该活动的开展有力地促进了全年经营目标的完成。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 5月18日，公司2005年班组建设工作会隆重召开。公司领导张毅、彭绍雨、陈光元、曾玉国，总经理助理李绍君、张伟，以及有关单位负责人、基层管理者代表100余人参加了会议。会议听取了总经理助理张伟代表公司所作的《打造一流班组，提高经济效益，培育四有队伍》的工作报告。隆重表彰了29个优秀管理班组，4位基层管理先进代表上台作了经验介绍。最后彭绍雨总经理作了题为《提高管理素质，增强适应能力，构筑起公司持续发展的坚定基础》的重要讲话。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 4月12日，硬面材料分厂技改工程重点项目之一的“堆焊药芯焊丝生产线”正式通过验收。该生产线的安装完成，填补了生产堆焊药芯焊丝产品的空白，拓宽了国内外市场，全年已销售该产品20吨左右，取得了明显的经济效益。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 为彻底改善生产分厂内部作业环境，提高生产现场的综合管理水平，公司决定，将矿用合金分厂作为2005年内部环境整治工作的试点单位。为此，3月2~3日矿用合金分厂分别召开了党政工团宣传动员大会和动员实施大会。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司质量检测中心荣获了中国实验室国家认可委员会颁发的认可证书。这一殊荣证明我公司质量检测中心达到了CNAl/AC01 ;2003《检测和效准实验室认可准则》(等同ISO/IEC17025\r\n  :1999《检测和效准实验室能力的通用要求》）的技术要求。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2004年</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△“长城”牌硬质合金荣膺“中国名牌产品”</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">9月1日，“中国名牌暨质量管理先进表彰大会”在北京人民大会堂隆重召开。公司“长城”牌硬质合金荣列“2004年中国名牌产品名单”，成为公司发展史上的一个重要成就。9月29日，公司隆重举办“长城牌”硬质合金荣获“中国名牌产品”庆祝大会。省市有关领导、兄弟企业领导、客户单位以及银行、报社、电台等嘉宾和公司班干部职工共370人参加了会议。同时，公司还荣获了“全国用户满意企业”称号，“长城”牌硬质合金荣获“全国用户满意产品”称号，这是全国硬质合金行业唯一获此殊荣的企业和产品。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△关闭两条钨冶炼生产线，彻底解决环境污染问题</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 为彻底解决废气、废水、废渣对环境的污染问题，实现达标排放，创优良人居环境，公司按照市委市府要求，全面开展了工业污染治理和环境综合整治工作。11月6日起公司对冶炼分厂101、106两条钨冶炼生产线实行永久性关闭。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△公司销售收入首次突破10亿元大关</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 2004年，是公司面临内外困难最大的一年。面对原辅材料价格上涨，银行信贷政策调整、公司流动资金紧缺等严峻形势，公司加大货款回笼力度，压缩各环节资金占用，全体职工紧紧围绕年度目标，千方百计克服困难，保持了生产经营规模稳定，基本完成了年度经营目标，销售收入首次突破10亿大关，达14亿元，同比增长45.9％。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△赣州海创钨业公司当年建成，当年赢利</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 4月28日，自硬公司与赣州特精钨钼业有限公司合资的赣州海创钨业有限公司正式成立。海创公司的成立体现了“内引外联，优强主业”的公司发展战略，当年建成，当年赢利135万元，成为公司重要的钨原料供应基地，将对自硬公司的可持续发展产生深远的影响。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△压力烧结炉成功通过鉴定</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 继喷雾干燥塔研制成功后，4月27日，石蜡工艺转换的又一重点设备ZY50/50/130压力烧结炉通过专家鉴定。该炉的几项重点指标均达到国际先进水平，成果的整体技术处于国内领先水平。压力烧结炉的研制成功标志我公司在重大关键设备的自主研发上取得了重大突破。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△四川省环保工作现场会到我公司参观视察</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 2月23日，在省委书记张学忠、省长张中伟的带领下，参加四川省环境保护工作现场会的代表们共149人到我公司参观视察，公司董事长、党委书记张毅向省市领导和与会代表汇报了公司污染治理及环境保护工作情况，省委书记、省人大常委会主任张学忠，省委副书记、省长张中伟在现场做了重要讲话，对公司环境污染治理成绩给予了充分肯定和高度评价。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△公司改制工作进入实施阶段</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 11月22日，公司在研发中心报告厅召开改制工作会。会议传达了自贡市国有企业改革改制攻坚领导小组关于自硬公司改制的会议精神以及公司改制领导小组结合市里精神形成的改制工作安排意见。公司党委书记、董事长张毅，总经理彭绍雨作了重要讲话，对统筹安排改制、稳定、经营工作提出了要求。这次会议，标志着公司改制工作进入实施阶段。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△BKXS产品喜获大幅增长</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司“长城”牌《商标国际注册马德里协定》申请注册成功。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△成都公司迎来十周年华诞</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 12月28日，成都公司隆重举行了建成十周年庆祝大会。龙泉驿区区委、区府、人大和开发区管委会负责人，自硬公司领导、技术工程人员、客户代表等200多人参加了庆祝大会。成都公司是自硬公司根据“立足自贡，拓展成都；辐射全国，走向世界”的发展战略而兴建的跨世纪重点工程。经过十年征程，成都公司共完成固定资产与配套项目投资2亿元，累计实现销售收入4.87亿元，共生产并销售产品1959吨，累计向国家上缴税金1160.15万元，并首次实现扭亏为盈。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△文峰山职工集资房建设提前一年全面完工</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 6月18日，设备基建部组织专家和有关部门对7、8号职工集资房进行竣工验收。这批集资房的完工，标志着公司集资房建设成为历史。文峰山家属区职工集资房共规划建设336套，从2000年开始，原计划用5年工期完成，至此，实际只用了4年工期，提前一年全面完工，职工住房条件大为改善。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2003年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 在由《互联网周刊》与国家信息化测评中心共同发起并组织的2003年中国信息化500强调查和测评活动中，我公司因推行ERP信息管理系统成效显著，形成了良好的发展创新能力，被列为“中国信息化500强企业”。在全国企业信息化高层论坛上，我公司应邀做了经验介绍。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 由ISO14001环境管理体系和OHSAS18001职业安全健康管理体系整合形成的环境安全健康管理体系（ESH）正式通过了中国质量认证中心（CQC）的第三方认证审核，我公司成为西南地区第一家同时获得ISO14001和OHSAS18001管理体系认证的大型国有企业。认证的获得，将有力地提升公司以人为本的企业经营理念和企业品牌形象，消除公司在产品出口方面的贸易壁垒，并使公司管理模式符合国际标准和惯例。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司领导班子民主生活会于11月11日成功召开。会议以“增进团结，改进工作，促进企业改革与发展”为主题，在自贡市委、市府领导的直接参与下，公司领导认真回顾思想状况，开展批评与自我批评，达到了充分沟通，增进理解，明确目标，统一意志的良好效果，为推动企业加快发展打下了坚实的领导基础。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 就在我国“神舟五号”发射成功的同一天（10月15日），我公司自行研制的混合料制备中心喷雾干燥塔试车成功。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 截至到10月，公司新申请专利8项，为历史上专利申请数最多的一年，体现了公司的技术创新水平。同时，还对已申请的7项专利进行了规范、实时的维护和管理。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司副总经理、工会主席曾玉国作为中国工会十四大代表，参加了在北京举行的中国工会十四大会议，这在我公司历史上是首次。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司总经理彭绍雨撰写的《我任国企总经理》一书由香港天马图书有限公司出版。该书是我公司历史上第一位高层领导亲笔撰写的管理丛书，它通过作者任总经理四年的管理实践，真实反映了国有企业的生存困境，凸现了作者的经营管理思想，为自硬公司的发展历程留下了宝贵的历史资料。原国家经贸委主任、现中国企业联合会、中国企业家协会名誉会长袁宝华同志为该书欣然作序。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司今年的重点投资项目--硬面材料中心工程正式投入生产。该工程仅用短短的6个月时间就建成投产，创造了公司有史以来工程建设的最快速度。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 我公司取得四川省出入境检验局授予的“出口商品一类检验监管企业”资格，在内江出入境检验检疫地区为首家。从此，公司将享受商检“绿色通道”待遇。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司进入2002年度中国有色金属工业销售前50名，我公司排在第45位。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 经国家对外贸易经济合作部批准，自硬公司在美国设立“自贡硬质合金国际销售有限责任公司”。该公司是自贡市第一例海外资本投资，主要面向北美及南美地区销售硬质合金及钨钼制品并提供相关服务。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司“长城”牌《商标国际注册马德里协定》申请注册成功。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司董事会颁文，正式成立公司董事会战略委员会、提名委员会、审计委员会、薪酬与考核委员会四个专门委员会。除薪酬与考核委员会由3人组成外，其余委员会均由5人组成。同时，公司董事会还颁发了各专门委员会实施细则。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司成立了公司体制改革领导小组，改制领导小组的成立，标志着公司产权制度改革正式启动。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自3月起，人力资源的薪资管理、绩效管理、招聘与配置、培训管理、职位管理等八个方面制度逐步建立和实施，网点会计人员全面实现本地化，标志着公司传统的人事管理向现代化、战略性的人力资源管理迈进。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△2月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司混合料制备中心进行了自制喷雾干燥塔塔体吊装。“喷雾干燥塔的研制”是公司重点招标项目，共投资650万元。整个喷雾干燥塔的安装工作于5月底完成，而此次塔体吊装，则标志着该设备安装的开始。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司在招待所举行了子弟学校整体剥离移交自贡市教育局管理签字仪式，公司董事长张毅和市教育局副局长杨晓春分别在《子弟校剥离移交协议》上签字，标志着公司正式结束了30多年的厂办校历史，社会职能剥离取得突破性进展。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自贡科瑞德新材料有限责任公司举行隆重的揭牌仪式。科瑞德是公司首次引入民营资本，与自贡长城工具公司、自贡市鼎立房地产开发公司、四川大学自然人等合资设立的控股子公司。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2002年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司下发《管理制度汇编》，它为公司推进管理标准化工作打下了坚实基础。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△11月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司进行了第一次工资集体协商。企业和工会依照法定程序，根据人数对等的原则，各派出７名代表组成了工资协商的机构。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △10目</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司电能增容主体工程结束，其新的主变及其新的操作控制系统正式投入运行。这标志着我公司的供电能力已达20000KVA以上。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发《高级技师、技师职位竞聘管理办法》，它是我公司制定的首次聘请高级技师的管理规范。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 乌兹别克斯坦专家来我公司作学术讲座与交流。它是公司首次利用政府基金开展的引智工作成果，此外，还引进了日本专家到我公司进行技术交流。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发《精细管理基本规范》。该规范进一步明确了精细管理的标准、目标及战略举措。促使“精细管理重创新，调整结构促优强”工作方针成为公司员工的自觉行为。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发《职工关爱扶助金条例》，它是公司历史上第一个完整规范的困难职工帮扶制度。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司党委在文化中心礼堂召开了干部队伍建设工作会。中层以上领导、各分会主席、职代组长、团支部书记共120多人参加了会议。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司博士后工作站发出《公告》，公开向社会招聘博士后研究人员。经招聘现已分别有中南工大和重大的2名博士后进站开始工作。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司隆重举行了2002年领导管理族绩效目标任务书签字仪式；</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发了《自硬公司博士后工作站管理办法（暂行）》和《2002年生产作业岗位劳动定额定员标准》。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司召开第四次技术创新大会。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司隆重举行了企业博士后科研工作站授牌仪式。企业博士后工作科研站的设立，在自贡市是第一家。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △2月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发了《二00二年绩效考核实施办法》，这标志着公司正式实施人力资源管理与开发（ZY-HR）体系。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司决定与自贡市灯泡丝材厂组建由我公司控股的“自贡长城钨钼高新材料有限责任公司”，长城钨钼公司的成立，标志着公司在资本运营方面迈出了实质性步伐。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司一次性通过了中国进出口质量认证中心进行的ISO14001环境体系现场审核，这标志着公司已获得国际贸易的“绿色通行证”。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2001年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司按照ISO9001：2000版标准建立的质量管理新体系，通过了英国BSI太平洋公司评审认证，表明公司质量体系转换工作目标已经实现。此前，新体系已通过了三峡质保中心的认证。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △11目</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 中央电视台《焦点访谈》栏目组专程来我公司采访报道“三讲”活动。公司是四川省首批开展“三讲”学习教育活动试点单位，在全省国企“三讲”活动总结大会上，作为先进单位的代表作了经验介绍。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △10月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司第五次党代会隆重召开。大会听取和审议了党委和纪委工作报告，选举产生了公司第五届党委和纪律检查委员会。分别由张毅任党委书记，陈光元任纪委书记。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△9月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 由自硬公司和美国亚西泰克公司合资组建的“中美合资自贡亚西泰克高新技术有限责任公司”正式宣告成立。这是我公司推进体制、机制创新，内引外联，探索产权制度改革的一项具有突破性意义的具体实践。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △8月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 陈越同志获自贡市“科技杰出贡献奖”。受到市委、市政府的隆重表彰，获奖金20万元。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁发《“十五”发展规划纲要》。《纲要》确定了公司“十五”期间的发展思路、发展方向及发展目标与措施，提出了“内引外联，优强主业”的发展战略。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司与生产分厂、自办网点等签定了《资产管理责任书》，实行二级单位资产管理责任制。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司硬质合金深加工项目交付使用。工程首次采用了全钢架结构厂房等多项新技术，仅用六个月时间即建成投产，创造了公司技改项目建设的新记录。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司公开招聘中层干部，公开选拔后备干部。对外招聘中干和公开选拔后备干部是首次进行，开创了改革公司干部选拔和任用方式的先河，促进了用人观和人力资源观的进一步转变。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 公司颁布实施《首问负责制暂行规定》和《投诉追究责任制暂行规定》。规定的颁发实施对严肃公司工作纪律，改进作风，提高工作效率，强化管理起到了积极的促进作用。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2000年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 中共中央委员、全国政协常委、中国质量管理协会会长陈邦柱在中质协副会长解艾兰陪同下，赴自硬公司考察质量管理工作。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △5月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司研究所研究二室主任、教授级高工李家杰荣获“全国劳动模范”荣誉称号。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △4月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司在成都龙泉召开了加快成都分公司发展工作会。力争用3-5年的时间，把成都分公司建成高新技术产品、深度加工产品、工具配套产品的科研开发基地和生产中心，成为自硬公司新的经济增长点和主要支柱。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △3月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 四川省企联合会专家组莅临自硬公司举办“WTO与西部大开发专题讲座”。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">1999年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 投资270万元，占地1000平方米，建筑面积3340平方米的职工文化中心正式开业，文化中心集图书馆、文化茶园、健身房等多功能为一体，成为公司精神文明建设的重要阵地。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △1月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司召开ZY-MIS实施动员大会，启动ERP项目实施工作，从而揭开了创新管理，提升公司管理水平，增强企业核心竞争力的序幕。</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">1998年</span>\r\n					</p>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">△12月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司年出口创汇额首次突破3000万美元。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △7月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自贡硬质合金厂转制为国有独资有限责任公司，公司举行了隆重的成立暨揭牌仪式。更名为“自贡硬质合金有限责任公司”。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> △6月</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 自硬公司实行建厂以来最大的一次机构改革，管理部门、中干职数、管理人员精减1/3。</span>\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p>\r\n						<span style="font-family:NSimSun;font-size:14px;line-height:2;">2009年05月06日</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style="text-indent:2em;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;">\r\n	<br />\r\n</p>');

-- --------------------------------------------------------

