INSERT INTO `gonsiwenhua` (`id`, `content`) VALUES
(1, '<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">坚持“质量第一，信誉至上”</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<strong><span style="font-family:SimSun;">二、企业文化</span></strong><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><span style="color:#333333;font-family:宋体;line-height:24px;font-size:14px;"><span style="font-family:SimSun;">传承海洋文化</span><span style="font-family:SimSun;"> 创造健康伟业</span></span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><strong><span style="font-family:SimSun;">三、企业精神</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">传承，合作，和谐，激情。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>四、企业经营理念</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">质量是根，诚信为本。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">五、企业价值观</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">创新，奉献，高远，荣誉。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">六、员工品德标准</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;">忠诚，敬业，团结，勤奋。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">七、公司愿景</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">致力于打造中国海产干品第一品牌！</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">八、公司使命</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">促进中国海洋经济发展，为人们提供卫生健康，品质优良的海产珍品，携领全国海产企业，繁荣中国海洋经济产业！</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">九、管理理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">管理人性化，行为严谨化，决策科学化，价值最大化。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">十、用人理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">以德为先，以能为本，量才而用，唯才是举。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">十一、服务理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">以家待客，以客为尊，耐心友善，热情周到。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<strong><span style="font-family:SimSun;">十二、企业形象理念：体现了企业的主体色</span></strong><br />\r\n<span style="color:#333333;font-family:宋体;line-height:24px;font-size:14px;"><span style="font-family:SimSun;">“深蓝”––“海蓝”––代表大海</span><br />\r\n<span style="font-family:SimSun;"> “浅蓝”––“天蓝”––代表天空</span><br />\r\n<span style="font-family:SimSun;"> “海阔天空、发展无限”寓意着公司高瞻远瞩，胸怀全球，在国际市场上劈波斩浪，雄姿翱翔，成为世界级海产干品航母。</span></span><br />\r\n<br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>十三、诚实守信</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">我们最重要的二大财富是员工、声誉。如二者之中任何一项遭到损害，最难重建的是声誉。我们将完全遵循我们国家的法律、公司规章和职业道德准则，诚实正直，坦率待人，始终坚持公司的价值观和原则。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>十四、社会责任</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">我们时刻不忘关爱社会、回馈社会，以身作则，承担企业社会责任，推动中国海产行业的健康发展。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><span style="font-family:SimSun;">" style="width:100%;height:150px;"&gt;</span><strong><span style="font-family:SimSun;">一、企业宗旨</span></strong><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">坚持“质量第一，信誉至上”</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<strong><span style="font-family:SimSun;">二、企业文化</span></strong><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">传承海洋文化 创造健康伟业</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><strong><span style="font-family:SimSun;">三、企业精神</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">传承，合作，和谐，激情。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>四、企业经营理念</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">质量是根，诚信为本。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">五、企业价值观</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">创新，奉献，高远，荣誉。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">六、员工品德标准</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;">忠诚，敬业，团结，勤奋。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">七、公司愿景</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">致力于打造中国海产干品第一品牌！</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">八、公司使命</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">促进中国海洋经济发展，为人们提供卫生健康，品质优良的海产珍品，携领全国海产企业，繁荣中国海洋经济产业！</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">九、管理理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">管理人性化，行为严谨化，决策科学化，价值最大化。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">十、用人理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">以德为先，以能为本，量才而用，唯才是举。</span><br />\r\n<br />\r\n<strong><span style="font-family:SimSun;">十一、服务理念</span></strong><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">以家待客，以客为尊，耐心友善，热情周到。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<strong><span style="font-family:SimSun;">十二、企业形象理念：体现了企业的主体色</span></strong><br />\r\n<span style="color:#333333;font-family:宋体;line-height:24px;font-size:14px;"><span style="font-family:SimSun;">“深蓝”––“海蓝”––代表大海</span><br />\r\n<span style="font-family:SimSun;"> “浅蓝”––“天蓝”––代表天空</span><br />\r\n<span style="font-family:SimSun;"> “海阔天空、发展无限”寓意着公司高瞻远瞩，胸怀全球，在国际市场上劈波斩浪，雄姿翱翔，成为世界级海产干品航母。</span></span><br />\r\n<br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>十三、诚实守信</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">我们最重要的二大财富是员工、声誉。如二者之中任何一项遭到损害，最难重建的是声誉。我们将完全遵循我们国家的法律、公司规章和职业道德准则，诚实正直，坦率待人，始终坚持公司的价值观和原则。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span><br />\r\n<span style="color:#333333;font-family:SimSun;font-size:16px;line-height:24px;"><strong>十四、社会责任</strong></span><br />\r\n<span style="color:#333333;font-family:SimSun;line-height:24px;font-size:14px;">我们时刻不忘关爱社会、回馈社会，以身作则，承担企业社会责任，推动中国海产行业的健康发展。</span><br />\r\n<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"> </span>');

