--
-- 表的结构 `hudon`
--

CREATE TABLE IF NOT EXISTS `hudon` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(80) NOT NULL,
  `call` varchar(80) NOT NULL,
  `email` varchar(80) NOT NULL,
  `content` text NOT NULL,
  `time` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- 表的结构 `huodon`
--

CREATE TABLE IF NOT EXISTS `huodon` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(80) NOT NULL,
  `content` text NOT NULL,
  `status` varchar(50) DEFAULT '',
  `time` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

--
-- 转存表中的数据 `huodon`
--

INSERT INTO `huodon` (`id`, `title`, `content`, `status`, `time`) VALUES
(1, '我市开展“3·15国际消费者权益日”宣传纪念活动', '<span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;">3月15日，市消委会在霞山广百商业广场隆重举行“3•15国际消费者权益日”宣传纪念活动暨“湛江消费维权网”（www.gdzj315.com）启动上线仪式，并组织全市30家企业进行“诚信承诺”宣誓签名。市政府副秘书长、市消委会会长刘兵等出席了活动。</span><img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506225247_80026.jpg" alt="" width="200" height="133" title="" align="left" /><span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;">企业代表进行“诚信承诺”签名</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;">市工商局、质监局等40多个职能部门组织了100多名工作人员以及5个相关行业协会人员和“3•15”律师团、专家团等进场宣传消费维权法律法规和相关消费维权知识，接受消费者投诉咨询，解答有关消费维权的疑难问题，教育指导、提供真假商品识别。活动现场还组织湛师等大学生志愿者开展消费维权服务活动，通过“湛江消费维权网”微博直播当天的活动情况。</span> \r\n</p>\r\n<p>\r\n	<img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506225323_83083.jpg" alt="" width="200" height="133" title="" align="right" /> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;"><span style="font-family:NSimSun;font-size:14px;line-height:2;"> 据了解，“3•15”国际消费者权益日期间，我市各地工商部门分别进行了假冒伪劣商品集中销毁行动。各级消委会也纷纷在当地举办现场宣传咨询活动，联合有关部门现场接受消费者投诉咨询，大力宣传消费维权工作，积极引导消费者维护合法权益。据统计，全市“3•15国际消费者权益日”系列宣传咨询活动共发出宣传资料3万多份，参加咨询的群众达5万多人次，现场受理消费者投诉90多宗并现场解决30多宗，使“3•15”维权观念深入人心，营造了诚信“3•15”的社会氛</span><span style="font-family:NSimSun;font-size:14px;line-height:2;"></span></span>\r\n</p>', '是', '2016-05-17'),
(2, '硇洲岛微生物和矿物元素丰富，是鱼虾蟹生活的天堂', '<span style="font-family:NSimSun;font-size:14px;line-height:2;">打造湛江海味品牌网店</span>\r\n<p>\r\n	<span style="color:#4C33E5;"><span style="color:#000000;font-family:NSimSun;font-size:14px;line-height:2;">“要让湛江海味更快地飘香世界，必须要有一个世界性的营销网络，网络无国界，湛江顺贸易海味不但要在地上卖，更要在网上售。”说干就干，湛江顺贸易海味网店应运而生，成为湛江最早在网上开店的海味经营者之一。湛江顺贸易坚信，网上销售可能是今后发展的重要方向之一。</span><br />\r\n<span style="color:#000000;font-family:NSimSun;font-size:14px;line-height:2;"> 质量是销售的保证。湛江顺贸易非常注重开发产品，先后推出硇洲虾干系列，硇洲“一夜埕”甘香鱼干、硇洲蚝豉等特色产品，这些产品均由“钱氏海味秘制工艺”加工而成，奠定了品牌的基础。2006年湛江“红鱼干”事件中，湛江顺贸易的系列产品全部通过通过质监局检测，达标率为100%。</span><br />\r\n<span style="color:#000000;font-family:NSimSun;font-size:14px;line-height:2;"> 湛江顺贸易的努力赢得了社会的赞誉和尊重。2006年、2007年，她被全国城市农贸中心联合会评为水产品类“全国优秀经销商”，被授予“21世纪中华女性工商管理成功人士”的称号。湛江顺贸易海味店获得“十大特色名优旅游产品”等荣誉，湛江顺贸易更是光荣地成为霞山区政协委员、霞山水产品批发市场水产商会副会长。</span><br />\r\n<span style="color:#000000;font-family:NSimSun;font-size:14px;line-height:2;"> “湛江成为全国首个被授予‘中国海鲜美食之都’称号的城市，我对湛江海味的前景充满信心。”目前，湛江顺贸易正在申请国家专利技术工艺，她与本地高校海产品专家、资深海味加工艺人合作，将传统工艺与现代科学融为一体，提高海味品质，扬帆世界。</span><br />\r\n</span>\r\n</p>', '是', '2016-05-06'),
(3, '湛江市有限公司顺南贸易海产品有限公司参加第三届广东现代农业博览会', '<img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506230337_39344.jpg" alt="" /><span style="font-family:NSimSun;font-size:14px;line-height:2;"></span><br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:宋体;font-size:14px;line-height:24px;"><span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;">第三届广东现代农业博览会开幕式现场</span><br />\r\n<span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;"> 本届农博会以“展示成果，加强交流，促进贸易，推动发展”为主题，规模有所扩大，展销模式逐步转变，力求达到参展品种更多，质量更优，效果更好，影响更大，不断创新发展农业会展经济，打造会展农业新品牌，推动广东省农业农村经济发展再上新台阶。湛江市农业代表团共有170多人，参加展览企业35家，展示农海产品100多个。</span><br />\r\n<span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;"> 开幕式前，省长黄华华、副省长李容根、省政协副主席温兰子、农业部总经济师张玉香以及省农业厅、财政厅、水利厅等领导在市领导阮日生、伍杰忠的陪同下，兴致勃勃地参观了湛江展厅，阮日生现场介绍了湛江市农产品展示情况，省有关部门领导认为，湛江农产品展示丰富，具有热带亚热带农业特色，希望湛江以名、优、特、稀、安全农产品优势，打造农业品牌，推进湛江现代农业发展。</span><br />\r\n<span style="color:#333333;font-family:NSimSun;font-size:14px;line-height:2;"> 湛江市除了派出高规格的代表团，展区、展品也精心布置和选择，充分体现了湛江现代农业的精华。在A馆湛江展区内，除了展示有大幅的能反映湛江现代农业气势的照片、介绍湛江现代农业的《发展中的湛江现代农业》画册、纪实湛江市发展现代农业的VCD电视专题片《铸造湛江农业新辉煌》，还有湛江热带特色的各种水果、蔬菜及农海加工产品100多种，包括湛江农垦的雄鸥/勇士牌蒸青绿茶、红江橙、三叶牌菠萝罐头、丰牌白砂糖，徐闻的火龙果、巴厘菠萝、珍珠莲雾、木瓜、龙椒2313、华金甜8号玉米、特美龙椒、同乐线椒王、东北麻辣王、中椒106、珍贵椒王、超前大椒、粤宝8号尖椒、粤丰3号节瓜、长丰2号紫云茄、中椒105，雷州的万吉番薯、尖椒、长茄、蜜本南瓜、西洋小南瓜、鹰峰牌冰糖芦荟、伊齐爽牌菠萝罐头、茂德公牌南派香辣酱、雪仙牌白砂糖，遂溪的圆椒、沙古牌萝卜仔、下录极品番薯、三宝牌红米、佳劲酒、金坡牌金砂糖、甘岭牌一级绵白糖、银河富贵竹、发财树，廉江的茗皇茶、茗上茗茶、劳福茂茶、龙家湾牌有机鸭蛋、廉江红橙、红杨桃、珍珠石榴、良垌淮生薯、雅沙牌白砂糖，黄坡蒜头，东海的鱼翅瓜、番茄、青瓜、甜玉米，坡头的乾塘莲藕、红萝卜、威力神酒，麻章的欢乐家牌水果罐头、天然萃牌金丝瓜、香米、黑米、黄秋葵、金鹿酒，霞山的三餐牌象牙米、益健米、益香米、一号米，同时，湛江展位还展示了银浪珍珠加工系列产品、湛江对虾和罗非鱼加工系列产品、钱大姐硇洲鲍鱼、白花鱼肚、鱼翅干、鱿鱼干、瑶柱干、元贝、“一夜埕”等系列高档海产干品，这些展示产品在农博会A馆很抢眼，不少前来参观的客人或驻足观看，或留下倩影，深受青睐。</span></span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:宋体;font-size:14px;line-height:24px;"><img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506230425_53080.jpg" alt="" /><br />\r\n</span>\r\n</p>', '是', '2016-05-06'),
(4, '中国知名的广东湛江市顺南贸易有限公司海产品亮相2012湛江美食节', '<p>\r\n	<img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506230643_39185.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:宋体;font-size:14px;line-height:24px;">&nbsp;湛江特产送礼好选择</span>\r\n</p>', '是', '2016-05-06'),
(5, '霞山政协、残联领导和总经理钱普荣慰问合法谋生的残疾人', '<p>\r\n	<img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506230920_94778.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<strong>碧海银沙网讯</strong><span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;">（图文/特约摄影师</span><strong>&nbsp;马志军&nbsp;</strong><span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;">通讯员 梁 梅 编辑/</span><strong>青&nbsp;</strong><span style="color:#333333;font-family:宋体;font-size:16px;line-height:24px;">风 蔚 青）今日，受霞山区区委书记杨柔彦和区委副书记、区长罗建君的委托，区政协联合区残联，再次来到雷州市客路镇慰问包地种养劳动求富的残疾人。而在区政协王玉珠主席及区残联等领导的号召下，中鑫公司、广东宝诚矿业公司、粤海饲料公司、金源大酒店等企业老板又一次向这些残疾人捐款，资助他们创业，而湛江市钱大姐海产品有限公司总经理钱普荣还向他们捐款和捐献了一批冬衣。</span>\r\n</p>', '是', '2016-05-06'),
(6, '湛江海产品勇闯国内市场', '<p>\r\n	<span style="font-family:NSimSun;font-size:14px;line-height:2;color:#333333;">近日，在湛江各大新闻媒体可以看到湛江市钱大姐海产品有限公司正在不断招兵买马，并高薪聘请国内营销界传奇人物、知名品牌推广专家——冯源先生担任营运总监，以专业的营销精英团队为钱大姐公司开展全国品牌策划和推广，全力将湛江人熟悉的“钱大姐”打造成中国海产品即食休闲产品领军企业。</span>\r\n</p>\r\n<div style="margin:0px;padding:0px;color:#333333;font-family:宋体;font-size:16px;">\r\n	<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 据记者了解，海鲜传统上一直以鲜活、冷冻和干制品等经营方式存在，近年来即食、物理冻干等采用现代技术手段的海产品开始出现，并经不断完善，发展成现在的即食休闲类产品，业内人士将其称为海珍品“第四种经营业态”。目前,湛江地区规模最大的海味干品批发龙头企业--湛江市钱大姐海产品有限公司正在大力开拓海产品即食休闲食品系列，公司新推出的小包装即食休闲海产品自从在2012中国（湛江）海鲜美食第二届文化节上亮相，立即吸引了广大消费者和众多商家的目光，丰富了湛江市的海产品传统加工工艺和方法，填补了湛江地区市场的空白。 </span> \r\n</div>\r\n<div style="margin:0px;padding:0px;color:#333333;font-family:宋体;font-size:16px;">\r\n	<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 在我国，休闲食品早已不仅仅是儿童的“专利”，随着休闲食品种类越来越丰富，正在逐渐成为人们日常生活中的必备需求品，同时消费者对休闲食品的种类、品质、及营养的需求也不断增长。2012年，世界休闲食品市场销售额越过900亿美元。在中国，据统计，2012年全国休闲食品市场销售收入达到5000亿元，同比增长31%。但是，休闲食品的同质化倾向却越来越明显，产品类别单一，薯类、谷类占大半江山。“作为一个大且快速膨胀的市场，中国休闲食品市场实在不应少了海产品的身影。”业内人士预计，这种小包装、便携带、低价位、高营养的休闲海产品，在未来几年内的市场规模将不小于现在的普通休闲零食市场规模，中国海产品休闲食品必然成为未来十年最具发展潜力的黄金行业。</span> \r\n</div>\r\n<div style="margin:0px;padding:0px;color:#333333;font-family:宋体;font-size:16px;">\r\n	<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 越来越多的湛江企业家有了如此共识，将海产品深加工成易被大众接受的休闲产品，无疑为湛江海产品拓宽了与外埠消费者接触的渠道。湛江市钱大姐海产品有限公司在大量周密的市场调研基础上，依据国家统计局、中国海关总署、国家商务部、中国消费者协会、全国及海外多种相关报刊杂志基础信息以及各省市相关统计单位等公布提供的大量资料，对我国海产品休闲食品行业的供给与需求状况、消费市场情况、竞争格局等进行了分析，并对中国海产品休闲食品行业的发展预测与策略进行了深入研究，把握休闲食品消费现状与趋势，制定市场策略。</span> \r\n</div>\r\n<div style="margin:0px;padding:0px;color:#333333;font-family:宋体;font-size:16px;">\r\n	<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 湛江钱大姐海产品有限公司一方面想方设法提高自身的生产标准和质量，另一方面全力以赴地做好品牌经营。凭借其多项强势品牌，例如：中国海鲜美食之都推荐品牌、中国十大特色名优旅游产品、全国海产品行业诚信品牌示范单位、全国海产品质量和食品安全示范单位等多项国内顶级品牌优势，推出“原生态、纯天然”、“先品尝、后购买”令消费者放心的后购买模式。</span> \r\n</div>\r\n<img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506231216_50635.jpg" alt="" /> \r\n<p>\r\n	<br />\r\n</p>', '是', '2016-05-06'),
(7, '湛江海产品勇闯国内市场', '<img src="/biyesheji/plugins/kindeditor/php/../attached/image/20160507/20160507124842_16528.jpg" alt="" width="400" height="251" title="" align="" /><img src="/biyesheji/plugins/kindeditor/php/../attached/image/20160507/20160507124842_79120.jpg" alt="" width="400" height="261" title="" align="" /> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="color:#444444;font-family:''Microsoft YaHei'';font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#444444;font-family:''Microsoft YaHei'';font-size:14px;line-height:24px;background-color:#FFFFFF;">冰鲜海鲈鱼</span></span> \r\n</p>\r\n<p>\r\n	<span style="color:#444444;font-family:''Microsoft YaHei'';font-size:14px;line-height:24px;background-color:#FFFFFF;"><img src="/biyesheji/plugins/kindeditor/php/../attached/image/20160507/20160507125051_65851.jpg" alt="" width="400" height="508" title="" align="" /><img src="/biyesheji/plugins/kindeditor/php/../attached/image/20160507/20160507125051_69403.jpg" alt="" width="400" height="400" title="" align="" /><img src="/biyesheji/plugins/kindeditor/php/../attached/image/20160507/20160507125051_89466.jpg" alt="" width="400" height="320" title="" align="" /><br />\r\n</span> \r\n</p>\r\n<br />', '是', '2016-05-07'),
(8, '广东温度急降　罗非鱼防寒刻不容缓', '<table border="0" width="100%" class="tf ke-zeroborder" style="color:#000000;font-family:宋体，Verdana, Arial, Helvetica, sans-serif;font-size:12px;background-color:#FFFFFF;">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<img src="http://www.shuichan.cc/upload/news/n2008120511191073.jpg" border="0" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class="bw" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<span id="font_word" class="htd">\r\n				<div align="center">\r\n					<span style="font-family:NSimSun;font-size:14px;line-height:2;">大量罗非鱼被冻死，惨不忍睹</span>\r\n				</div>\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 从广东省气象台12月5日5时发布天气预报来看，湛江茂名等广东罗非鱼的主产区的最低温度是12-13度，韶关河源梅州三地的最低温度甚至已经降至5-6度。罗非鱼养殖防寒工作刻不容缓。如果只是短期的降温损失不会很大，但是万一今年又是像去年那样的暴冷天气，防寒工作又没有做到位，后果将不堪设想。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 为此，中国水产养殖网编辑整理了一些罗非鱼防寒防冻的措施，仅供各位养殖户参考。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 1、温泉水越冬。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 越冬池面积应根据水温、流量、地形及生产规模而定。越冬池水深以1.5-2米为宜，进水量和出水量要相近，以保持一定水温和深度。如温泉水水温很高，应先经蓄水池冷却到一定温度再注入越冬池，这种越冬方式及越冬密度较高。同时，由于水温较高，水量充足，可强化亲鱼饲养培育，促进亲鱼性腺早熟，提早进行繁殖育苗。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 2、电厂或其他工厂余热水越冬。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 我国各地的能源工业较多，每天有大量温水排放或被循环利用。这些水一般都未被污染，且水温都比自然水温高，如热电厂的冷却水就如此。当前利用温排水这一热源进行罗非鱼越冬保种和提早繁殖收到了较好效果。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 这样的水配合增氧措施，可使水中溶氧达到5～7毫克/升，水温一般在16～25℃之间。达到这样条件的水体，越冬成鱼、亲鱼的数量可按一般养殖而定。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 利用余热水越冬期间的投饵量视水温及鱼的吃食情况而定，饵料为粗蛋白含量为25％左右的配合颗粒饵料。投饵量一般占鱼体总重的1％～2％，平常日投2次即可，接近春季可投喂3次。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 当温水水温较高，经管道引入池中时，各养殖池可备有深井凉水管用于提供凉水调温。一般来说，投喂的饵料有豆饼粉、麸皮和人工配合饵料等都可以作为越冬饲料，日投饵量视水温及鱼吃食情况而定，温度越低，投饵量越小；反之，温度越高投饵量越大。水温在16～22℃，一般为鱼体总重的0.5％～1.5％。当养殖密度较大时，要定期排污，常用排污设备为胶皮虹吸管和小型轴流吸泥泵，每周排污1～3次。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 3、塑料搭棚土池越冬。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 越冬池选择在背风向阳、水质良好、水电方便的地方。越冬池最好选择东西向，长方形，面积60-120平方米，水深以2-2.5米，注排水方便。池面上用钢筋或竹木搭建拱形或人字形的棚架，棚顶离地面高度1.8-2米，其上覆盖两层塑料薄膜，外面压钉竹片，棚脚四周用泥土压实。这种越冬方式可利用地下水为水源，保持水温16-18℃，越冬效果好，成本低，易推广。如无地下水，可用水库、溪河水，通过电热器加热，提高水温，一般30立方米水体应配用3千瓦的电热器1-2个，此法适宜养殖户小面积越冬。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 4、修建玻璃越冬房越冬。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 越冬池为水泥地，面积以50-80平方米，水深以1.5-2米为宜。越冬池的四周围砌砖墙，顶上用透明玻璃遮盖而成越冬房。&nbsp;&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 5、加热水体越冬方法</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 主要有电加热和燃料加热两种越冬方法。电加热装置主要有稀离子水下发热器和电热线加热器，较适合在电力充足、供电正常并且电价较低的地区使用。燃料加热设备有专用炉和取暖锅炉等，适于在其他条件欠缺而燃料资源丰富、来源方便且价格低廉的地区使用。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 使用电加热器时，将发热器装在池子内，根据池子大小确定个数，以能保持水温为宜。例如，用电热线加热器加温越冬。制作材料多为无毒的高压聚乙烯塑料绝缘的电热线，热效率高40％。在室温为5℃时，用1只3千瓦的三相电加热器可使25立方米水体的温度保持在20℃左右。这种加热器必须在水中使用，严禁无水通电，否则会烧坏绝缘体。为防止加热器向上浮起，可适当增加重量。在池中的位置应离开池壁20厘米，离池底20厘米。为确保安全必须接地。发现电线或接头破损时，应及时更换修理。换水前须先切断电源。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 燃料加热，靠受热后水的对流将热水送入管道，热水流经铺设在越冬池内的加热管将池水加热，冷却后的管道水又流回炉内，被重新加热循环利用。&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 在生产中要根据越冬池面积的大小，确定锅炉的大小。也可以用炉灶直接加热越冬。在越冬池旁边建造炉灶，用燃煤直接给越冬池或越冬水缸等加热的方法，一般炉灶设在越冬池的下部，炉灶的烟道通过池底或池壁中的迂回并列烟道将水加热。烟道出口要有一定高度，以便加强火势和热量在烟道中的扩散流动。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">&nbsp;&nbsp;</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 所有的养殖户养殖户，要不惜资金，建温室鱼塘，或给鱼塘配备保暖设施，或在塘边打深水井。一旦遇到低温天气，即可启用备用设施，避免损失发生。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 所有的养殖户都必须立刻腾出人手检查养殖基地的电线是否存在问题，有没有断电存在。年初的很多罗非鱼养殖基地都因为大雪压断了电线，导致基地断电，无法给罗非鱼保温，大量的罗非鱼被冻死了。</span></span>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '是', '2016-05-08'),
(9, '明年，还要继续养殖南美白对虾吗？', '<table border="0" width="100%" class="tf ke-zeroborder" style="color:#000000;font-family:宋体，Verdana, Arial, Helvetica, sans-serif;font-size:12px;background-color:#FFFFFF;">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<img src="http://www.shuichan.cc/upload/news/n2008110610495355.jpg" border="0" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class="bw" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<span id="font_word" class="htd"><span style="font-family:NSimSun;font-size:14px;line-height:2;"> 经历了去年一整年的“灾难年”的南美白对虾，在今年年初又经历了一场极其罕见的大暴雪。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 大暴雪对难美白对虾产业的最初也是最根本的影响就是导致了各地对虾苗数量锐减，虾苗价格暴涨。无力承担高额的虾苗成本，不少养殖户直接选择了放弃对虾养殖。很多大的养殖基地也因为虾苗成本高饲料成本高，以及后期对虾发病严重等问题感觉压力特别大。更悲哀的是，过去的将近一年的时间里，南美白对虾的价格很少坚挺过，这让很多人心碎了。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 目前，南美白对虾的主产区仍然是东南沿海几个省份，江苏湖北等地的南美白对虾也都形成了一定的规模，可以说，南美白对虾的养殖范围已经遍布了大半个中国了，对虾养殖已经成了一个不折不扣的常规养殖品种了。过去一段时间也有一股风潮觉得“我国的南美白对虾产业供大于求”，再加上目前南美白对虾产业面临的困境，这不禁让很多人心里有这样的问题，明年，我们还要养殖南美白对虾吗？</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 答案是肯定的。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 首先，想一下，如果我们都放弃了养殖南美白对虾，会有什么样的后果？</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 想想我们的东北大豆吧，当年的东北大豆产业如日中天，结果被价格低廉的美国大豆冲击，而且，没有能抵挡住冲击。现在呢？美国大豆在中国市场横行霸道。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 其次，南美白对虾在国内市场还是有极大需求的，虽然说南美白对虾在一定程度上体现出了供大于求的局面。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 只要有需求，我们就要继续养下去。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 下面就是我们要怎么养南美白对虾的问题了，还有就是政府部门在南美白对虾产业环节中应该充当什么样的角色。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 怎么养南美白对虾，首先从技术层面上讲，这不是什么问题。现在的南美白对虾养殖过程中最大的问题就是如何防止大面积的疾病暴发，尤其是在东南沿海等地。我们知道，台风过后，养殖基地遭到严重毁坏不说，台风过后如果不采取恰当的措施，极易暴发大面积疾病的。台风暴雨过后,养虾池塘的各种理化因子会发生很大变化,主要表现在池塘溶解氧不足,水温突变,藻类死亡,水质变清,PH值、盐度突变。从而造成南美白对虾产生很大应激,导致虾病的发生。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 在今年年初的大暴雪之后，各地虾苗数量锐减，价格居高不下。很多对虾养殖户无力复产，不少地方政府为养殖户免费赠送了虾苗，但是对大多数人来说这仍然是杯水车薪。在对虾市场价格低迷的时候，很多养殖户亏本的情况下，政府是不是能出一些市场指导价建议价？在大多数人都亏本的情况下，如果政府能干预一下市场，这应该是很多人都愿意看到的。政府可以给出市场指导价建议价，或者让大的加工厂以“保护价”收购散户的对虾。还有一个办法就是政府搭台，搞一些对虾促销展销，希望能借此提高市民的消费积极性。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;"> 明年年初应该不会再有那么大的雪了，如果很多对虾养殖户今年能挺过去，明年，养殖对虾应该还是一个不错的选择。</span></span>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '是', '2016-05-08'),
(10, '5月6日湛江宝满市场冰鲜对虾行情及分析简报', '<a href="http://www.shuichan.cc/upload/news/n2016050623482419.png" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/n2016050623482419.png" /></a><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">行情分析：&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">时近5月中旬，又将进入新一年对虾产季，湛江宝满市场已开始有部份档口有新虾上市交易。目前主要虾源来自海南，规格以中小规格为主，据不完全统计，日供应量大约在10-20万斤之间。&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">从近几日价格来看，由于湛江当地大多数工厂仍未开工，虾价与去年基本保持持平，颜色较青的原料仍然较少，大多数为青白色，鲜度一般。</span><span style="font-family:NSimSun;font-size:14px;line-height:2;"></span>', '是', '2016-05-08'),
(11, '对虾到底应该喂几成饱？控料养殖有好处，但是要适度', '<a href="http://www.shuichan.cc/upload/news/n2016050622461286.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/n2016050622461286.jpg" width="500" /></a><br />\r\n<br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">山东沿海部分地区养殖户9成转养</span></b><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">水泥池工厂化养殖南美白对虾，以其亩产高、成功率高、效益高的“三高”特点成为众多养殖户追捧的新宠。在山东沿海地区，没有任何一个养殖品种的产业转型能和南美白对虾一样迅速：从2014年的少数人试养，到2016年初的九成转化率，只经历了短短的两年时间。现在大量的海水鱼棚、海参棚，都在热火朝天的改造中——增加池壁高度、更换透明篷布、加强增氧能力、装备加热设备；同时更多的专养南美白对虾的新棚，如雨后春笋般破土而出，更是吸引了来自全国各地的养殖能手前来创富。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">从事南美白对虾养殖的人越来越多，而养殖的成功率却越来越低。2016年初期，日照地区的养殖情况很不乐观，出现大量的排塘、掉苗、生长缓慢等问题，在笔者做技术服务期间，和众多养殖户探讨过出现这些问题原因是什么，他们的观点总结为：70%的养殖户归因于苗种质量不好，20%的养殖户归因于水质不好，10%的养殖户归因于自己操作的不当。养殖失败并不可怕，可怕的是不去总结失败的原因，而一味的把失败归因于苗种问题、饲料问题，不去总结经验教训，那下一茬的养殖成功率又能有多少呢？</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">笔者不才，和出现排塘、掉苗、生长缓慢等问题的养殖户进行了深度的交流和探讨，希望可以找到出现这些问题的原因，来帮助养殖户把“难”美白对虾养好，让南美白对虾不再“难”。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050622461687.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050622461687.jpg" width="500" /></a><br />\r\n<br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">对虾到底应该喂几成饱？</span></b><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">除去苗种问题（市场有很多苗种品牌，不可能都有问题）、水质问题（这个谈了也没有改进的办法），我这里主要谈的是投喂问题，很多养殖户对于控料都表现的如临圣谕一般，小心遵守而不敢逾越。养殖户现在交流不是比谁家虾子多大了、水怎么样了，而是比谁喂得少。就像养殖户自己认为的，南美白对虾的所有问题都是因为吃饱撑出来的，“我喂得少我骄傲”、“我喂得少我健康”，更有养殖户比专家教授还肯定的说“谁喂得多谁死的快”。事实真的是这样吗？控料在南美白对虾养殖中是一项很重要的技术手段，实施得好了，不但能养出健康的虾子，更能降低养殖成本，而一旦实施不好，造成的危害更是让养殖户闻风丧胆。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050622462089.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050622462089.jpg" width="500" /></a><br />\r\n<br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">控料对土池养殖的三大好处</span></b><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">首先讲一下控料，控料首先是在土池养殖模式中出现的概念（因为那时人们还不知道南美白对虾还可以养在水泥池中），控料在不同区域、不同养殖人员表现出不同的程度，我查阅了相关文献和野文，大约是指南美白对虾养殖饲料投喂量只要达到对虾6-8成饱即可，这点在我心里也是圣谕一般，不敢逾越，原因有三：</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">1、减少养殖水环境的污染：土池没有完善的排污系统，饲料残饵、粪便会集聚池底，对底质、水质造成不可逆的污染，导致氨氮、亚硝酸盐、硫化氢等有害物质积累，同时给有害细菌繁殖提供丰富的有机质，导致有害细菌爆发式增殖。控料可以减少对水体有机质的输入，减少对水环境的污染。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">2、充分利用水体中天然饵料：土池水面一般较大，露天养殖，水体中含有丰富的单胞藻类和南美白对虾适口的原生动物，控料可以让南美白对虾主动捕食单胞藻类和原生动物，不但可以训练对虾体质，同时也很好的减轻了养殖后期藻类和原生动物对水体溶氧的消耗。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">3、控制成本：本来土池养殖成功率就很低，理所当然，控料是为了节约成本投入。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">那么问题来了，水泥池养殖，你的南美白对虾吃了几成饱呢？</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050622462549.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050622462549.jpg" width="500" /></a><br />\r\n<br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">过度控料的三大危害</span></b><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">我先讲讲控料过度会出现的危害吧，也是三点：</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">1、虾苗生长速度慢、整齐度差（废话，都不给我们吃饱，还让我们长得又快又整齐，没饿死就不错了）。这个实例许多卖虾苗的一定感同身受：很多养殖户养了90天的南美白对虾才60-70头，而且大小不整齐，他就会和你说“你家苗不好”，然后你说“别人和你一起的苗现在都30头了”，“他的苗和我的苗在苗场不是一个池子的”，谈话到此结束了。苗都养了三个月了，还怪苗不好，苗贩子只好“呵呵”了。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">2、长期严重控料，对虾体质下降。对虾会摄食粪便和藻类（对虾摄食饲料产生的正常粪便长度小于5毫米、颜色和所投喂饲料颜色接近，出现粪便长度过长、颜色乌黑或者发绿，是摄食粪便或藻类导致的），藻类含有藻毒素、粪便中有害细菌含量高，长时间会造成藻毒素中毒和肠道细菌病。这时对虾活力差，出现拖便，养殖户会认为是肠炎，马上停食3-5天，你是不把虾子饿死不罢休啊！</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">3、中后期出现的软壳死、对虾抱食软壳虾：长时间饥饿，加之摄食藻类和粪便，导致对虾体质较弱，在分苗后，水环境剧烈变化，导致掉苗、排塘；有些体质好的，躲过了这一劫，分苗后继续严重控料，等到倒藻时，水环境的剧烈变化还是要了它们的命；即使安全的渡过了倒藻期，体质一直再锲而不舍的虚弱下去，等到蜕壳的时候，终于筋疲力尽而死，这正好拯救了一些体质好的虾子，它们实在是饿急了，见到“剥了壳”的虾肉，它们理所当然的就去“进食”了，这就导致了中后期出现的软壳死、对虾抱食软壳虾的情况。等养殖户发现掉苗了，第一反应还是停食，阿弥陀佛。众所周知，过度投喂会导致肝胰脏负担加重、出现肠炎，但比起控料过度的严重性，孰轻孰重，养殖户自会明断。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050622462921.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050622462921.jpg" width="500" /></a><br />\r\n<br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">工厂化养殖中，怎样的投喂量算合适？</span></b><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">既然水泥池养殖可以很好的排除污染物，更好的人为调控水体的环境，那我们为什么不让虾子吃饱呢（八成饱足矣）？如果你敢喂，那你就继续向下看吧！笔者见识浅薄、人微言轻，对于怎么来判断饲料投喂量的多少，愿献上微言，以抛砖引玉。也分三点吧（按一天五次投喂为例）：</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">1、饲料投喂三小时后观察气头和池壁边是否有虾子浮起：饥饿的虾子会上浮啃食池壁上的藻类，如果有大量虾子上浮，说明池底的粪便都被吃完了，投喂量严重缺失，需要加料，维持投喂三小时后有少量虾子上浮的投喂量即可。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">2、放置料台，饲料投喂后2小时，观察料台中的虾子的肠道：肠道内含物颜色和饲料颜色接近且饱食的比例在一半以上，说明投喂量适度，否则是缺料，需要加料。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">3、排污时检查粪便：如果排不出或者排出少量粪便，说明缺料；查看粪便状态，如果出现长度增加和大量颜色乌黑的粪便，说明缺料，需要加料，维持正常粪便比例大于异常粪便即可。这三种方法基本上可以准确的判断虾子吃没吃饱，同时建议在喂食后两个小时时进行一次排污，因为南美白对虾饱食后两个小时就可以把肠道排空，这时水体中粪便较多，进行排污可以减少对虾对粪便的摄食，同时缩短粪便在水中的浸泡时间，减少粪便中有害物质对水体的污染。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">讲到这里，也许很多人会说“你的意思就是使劲喂喽”，请不要误解我的意思，整篇文章我都在强调“投喂适度”，不要让你的虾子因为吃不饱而丧命就好。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">南美白对虾的养殖，就和赌博一样，没有固定的模式可以追寻。养殖失败总是有原因的，但是如果不在失败中吸取经验教训，不去学习新的观念和技术，只会固步自封、人云亦云，那只能在失败中徘徊，永远找不到成功的出口。</span><br />\r\n<br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;">附表：不同体长对虾饲料按体重的投喂比（温度不低于28℃）</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050622463355.png" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050622463355.png" width="500" /></a><span style="font-family:NSimSun;font-size:14px;line-height:2;"></span>', '是', '2016-05-08');
INSERT INTO `huodon` (`id`, `title`, `content`, `status`, `time`) VALUES
(12, '五月份华南区域对虾病害预测及处理措施', '<table border="0" width="100%" class="tf ke-zeroborder" style="color:#000000;font-family:宋体，Verdana, Arial, Helvetica, sans-serif;font-size:12px;background-color:#FFFFFF;">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<img src="http://www.shuichan.cc/upload/news/n2016050514480343.jpg" border="0" width="520" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class="bw" style="font-family:宋体，Verdana, Arial, Helvetica, sans-serif;">\r\n				<span id="font_word" class="htd"><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">作者徐剑锋：工商管理硕士，从事水产饲料和动保产品经营管理二十多年，见证了中国对虾产业发展历程，现任湛江粤海水产生物有限公司总经理。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">5月份进入“立夏”和“小满”节气，气温逐渐上升，各地对虾养殖进入高峰期，对虾养殖的病害随着气温和水温的升高而增加，根据近几年的病害情况，5月份需要关注以下病害：</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">1、应&nbsp;激</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">危害：应激会引起对虾生理发生变化，使其非特异性免疫力减弱，对病菌或病毒的抵抗力下降，继而导致疾病的爆发。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">原因：气候变化（如大幅度降温）、水质突变（如“倒藻”）、生理突变（如蜕壳、病原入侵、淡化适应）、物理刺激（如拉网捕虾）、药物刺激（如使用消毒剂）&nbsp;。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">处理措施：在可能出现应激情况下，泼洒解毒应激灵或应激维C等抗应激类药物。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">2、池水倒藻、泛底、有害藻类繁殖</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">原因：有机质积累，水质出现富营养化。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">危害：水质变化大，不利于对虾生长；死亡后分解过程中产生氨氮、亚硝酸盐、硫化氢等有毒物质威胁对虾生存。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">处理措施：有害藻类浓度不大时，可排换水、使用富水美、红菌素等微生物制剂抑制其繁殖。有害藻大量繁殖时，先用药物杀藻；再使用有机酸解毒灵消除毒素；同时注意改良底质和重新培养。</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">3、氨氮、亚硝酸盐</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">原因：水温升高，对虾摄食量增大，饲料大量投喂，造成粪便多，含氮有机物多；池底淤泥过厚；水质混浊，水底溶氧不足等有关。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">预防措施：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）彻底清塘、消毒、避免有机物的大量积累。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）投饵不能过量。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）在养殖过程中保持水体的微碱性状态。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）池塘装上充足增氧设备，保持高溶氧水平。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）避免使用未发酵的有机肥，最好使用氨基酸有机肥等可溶性有机生物肥。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（6）经常使用硝化细菌、芽孢杆菌、光合细菌等有益微生物制剂，以调节水质。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（7）定期改底，防止池底积污发臭。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（8）定期在饲料中添加“乳酸菌”、“EM菌”、“酵母菌”等，增强对饲料营养的吸收利用，减少排泄物对水体的污染</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">4、蓝藻水</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">原因：水体富营养化或施肥不当，肥料中磷占比例过大，导致蓝藻大量繁殖。这种水体中的对虾长速缓慢、成活率低、发病率高。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">危害：蓝藻大量繁殖会造成其它藻类的死亡；蓝藻不易消化，被虾类摄食后，易引起肠道疾病；蓝藻死亡后分泌大量的藻毒素，对对虾机体造成伤害。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">蓝藻预防与处理：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）使用芽孢杆菌、光合菌等有益菌，有效抑制蓝藻繁殖。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）清淤；强氧化剂清塘；肥水时注意肥的类别，使用含磷量较少的肥类。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）配备使用水车式增氧机，开动水车式增氧机使池塘形成环流，有效控制蓝藻繁殖。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）控料，减少底部积污；使用沸石粉等改良底质。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）蓝藻严重时，可使用蓝藻分解剂、强氧化剂消毒杀灭蓝藻。药物使用时注意增氧、解毒；杀藻后换水补菌，引进新水等。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（6）EM菌加红糖，降低pH值，能抑制蓝藻繁殖。</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">5、肠炎、白便</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">病因分析：底质差；水质有害藻类繁殖过多，水色太浓，弧菌感染；虾苗问题。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">预防措施：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）定期投喂乳酸菌、酵母菌、多维等，定期保肝护肠解毒，可以起到预防效果。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）调好水质，做好改底工作。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）维持溶氧，维持藻类平衡，防止倒藻。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）定期检测弧菌，总菌和弧菌比大于30：1。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）选择优质的种苗。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/n2016050514515265.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/n2016050514515265.jpg" width="500" /></a><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">对虾白便</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">6、肝胰脏萎缩病</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">病因分析：出现肝胰脏萎缩，存在多种原因，主要集中在养殖环境水体恶化和对虾体质较弱这两个方面；弧菌问题是引起肝萎缩的最大原因。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">水质方面：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）近年来养殖海区的水质富营养化，有害藻类大量繁殖。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）养殖过程中过量投饵，造成有毒物质增加。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）养殖水质富营养化，倒藻等导致水中藻毒素的增加。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">导致对虾体质弱化，抵抗力变差的因素：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）苗种场就用抗生素，苗种体弱，抗逆力差。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）饲料原材料营养不均衡。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）某些微量元素缺乏。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）饲料存放受潮变质，导致含霉菌等致病菌超标。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）乱服抗生素、滥用化学药物。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（6）对虾体弱，苗种复壮不及时，后期积弱严重，损坏免疫器官、消化器官。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">预防措施：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）投放优质虾苗。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）做好调水工作：水不能太肥，pH不宜偏高。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）科学合理投喂饲料。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）定期使用活菌（芽孢杆菌、光合菌、EM菌等），保持藻相、菌相平衡。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）做好改底、解毒工作。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">治疗措施：</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）停止进排水，减料50%。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）泼洒维生素C&nbsp;+葡萄糖；加大增氧。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）下午泼洒聚维酮碘消毒。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050514542964.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050514542964.jpg" width="500" /></a><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">对虾肝胰脏萎缩病</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">7、棉花虾</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">患病虾表现为额剑和触角弯曲、变形，甲壳软薄、粗糙无光泽或残缺，肌肉白浊、不透明，失去弹性，故有人称之为乳白虾或棉花虾。通常表现为胃表皮、后肠、鳃、条纹肌、神经节、连接组织、血细胞、造血器官的炎症性病变，严重的表现为坏死。在整个养殖过程中，虾生长缓慢或停止生长。肠道上皮细胞微孢子虫是近年来引起南美白对虾生长缓慢的主要原因之一。南美白对虾感染肠道上皮细胞微孢子虫之后，最明显的特征就是生长速度会变得缓慢，导致对虾个体差异大、参差不齐。但感染了肠道上皮细胞微孢子虫的虾，并不会立即死亡，相反观察虾体，其肠道也是非常饱满，食欲正常，一样能正常吃料，肠、胃充满着食物，肝胰腺略微萎缩，发软，但颜色很深。从早期表面上看起来，虾并没有明显疾病症状，但就是不长个，偶尔伴随有白便。患病对虾也一直吃饲料，养殖户会把所有的时间和饲料都用于养殖这种患病而根本长不大的对虾身上，而到头来往往得不到有效的收获，造成巨大的经济损失。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">目前，还没有发现针对“棉花虾”治疗有效的措施，因此，做好预防工作非常重要。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（1）选购大品牌优质虾苗，经过严格检测，防止苗种携带。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（2）在发现该病的养殖区域，尽量少换水，如需换水，要经过消毒处理。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（3）养殖过程中经常调节稳定水质，防水变诱发发病。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（4）拌料投喂保健利肠道的添加剂，增强对虾免疫力。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">（5）定期改底，改善池塘底部环境。</span><br />\r\n<a href="http://www.shuichan.cc/upload/news/2016050514530540.jpg" target="_blank"><img border="0" alt="按此在新窗口浏览图片" src="http://www.shuichan.cc/upload/news/2016050514530540.jpg" /></a><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">棉花虾</span><br />\r\n<b><span style="font-family:NSimSun;font-size:14px;line-height:2;">8、总结</span></b><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">首先在养殖密度上，在保证一定的存活率的前提下，降低养殖密度，养殖大规格虾，养虾的利润仍然很高；当前南美白对虾养殖普遍存在一个成活率偏低的情况，在放苗后若遇到不良天气经常出现全塘虾死光，在养殖过程中由于底质恶化出现偷死，种苗的质量至关重要！合理的养殖密度，再配以高效、优质的饲料，对虾养殖成功的可能性将大大提高。</span><br />\r\n<span style="font-family:NSimSun;font-size:14px;line-height:2;">控制水质是技术的核心和成功养殖的关键。生态防控是对虾养殖必经之路，也是重要的可持续发展之路。</span></span>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '是', '2016-05-08');

