--
-- 表的结构 `jdtuku`
--

CREATE TABLE IF NOT EXISTS `jdtuku` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `tuku` varchar(50) NOT NULL,
  `href` varchar(50) NOT NULL,
  `status` varchar(50) NOT NULL DEFAULT '是',
  `time` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=6 ;

--
-- 转存表中的数据 `jdtuku`
--

INSERT INTO `jdtuku` (`id`, `title`, `tuku`, `href`, `status`, `time`) VALUES
(1, '海产食物，是指利用海洋动物作成的料理，包括了鱼类、虾类、贝类这些等等。虽然海带这类海洋生物也常是被料', '', 'http://qishuixian.com/biyesheji/pc/index.php', '是', '2016-05-26'),
(2, '中华料理中八个著名的海产品，分别是海参，鱼翅，鲍鱼，干贝，螃蟹，虾，乌贼，比目鱼。 另据旧时南货老人', '', 'http://qishuixian.com/biyesheji/pc/shangpinall.php', '是', '2016-05-26'),
(3, '中国通称的海参，包括所谓“海黄瓜”的全部动物或指干海参。狭义的仅指海参属。海参属是海参纲最大的属,包', '', 'http://localhost/biyesheji/pc/zuixingshangxian.php', '是', '2016-05-05'),
(5, '1111111111111111', '', '333', '否', '2016-05-05');

