CREATE TABLE IF NOT EXISTS `shangpin` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `pName` varchar(255) NOT NULL,
  `cId` smallint(5) unsigned NOT NULL,
  `pDesc` text,
  `status` varchar(50) DEFAULT '是',
  `news` varchar(50) DEFAULT '是',
  `hot` varchar(50) DEFAULT '是',
  `pNum` int(10) unsigned NOT NULL DEFAULT '1',
  `time` date NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `pName` (`pName`),
  UNIQUE KEY `pName_2` (`pName`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=41 ;

--
-- 转存表中的数据 `shangpin`
--

INSERT INTO `shangpin` (`id`, `pName`, `cId`, `pDesc`, `status`, `news`, `hot`, `pNum`, `time`) VALUES
(1, '翘嘴桂花鱼', 5, '<p>\r\n	<a target="_blank" href="http://baike.baidu.com/view/1048547.htm">翘嘴鳜</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，别称，桂鱼 </span><a target="_blank" href="http://baike.baidu.com/view/1210285.htm">桂花鱼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"> 胖鳜鱼 季花鱼 螯鱼 花鲫鱼。因水域的不同而生长速度差异悬殊。但总的来讲，江南地区比北方迅速。</span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506235133_26270.jpg" alt="" width="220" height="110" title="" align="right" /></span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">繁殖习性在天然水域中鳜的最大个体长度可达800毫米以上,重7.5公斤。性成熟较早,2龄性成熟,性成熟的最小个体210毫米,重250克。系分批产卵鱼类,一生可多次重复产卵。产卵期5-8月,少数可延伸至8月。多在夜间产卵,卵浮性,圆珠形,橙黄色,具油球。卵径1.2-1.4毫米, 受精后卵腹膨胀,卵径约为2毫米。怀卵量一般2.7-21.3万粒之间,怀卵的多少与鱼体的大小成下比,水温21-24℃经72小时即可孵化,初孵仔鱼长4.2-4.4毫米。肉食性底栖凶猛鱼类,营掠性摄食方式。主要捕食小型鱼类及虾类。初孵仔鱼待卵黄囊消失后即食大型浮游动物和其他鱼类仔鱼;体长40-50ypoy时,捕食小虾,体长95毫米时,除小虾外还兼食小型鱼类。摄食强度发夏季最强, 繁殖?</span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><img src="/biyesheji/plugins/kindeditor/attached/image/20160506/20160506235239_11793.jpg" alt="" width="667" height="500" title="" align="" /><br />\r\n</span> \r\n</p>', '是', '否', '否', 100, '2016-05-30'),
(2, '多宝鱼', 5, '<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">多宝鱼成鱼体长最大可长到75cm。体侧很扁，而且还是</span> \r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"> 卵圆形的身材，它的两眼在头部的左侧，它的长相不但奇特，而且多宝鱼皮下和鳍边含有丰富的胶质，头部及尾鳍均较小，鳍条为</span><a target="_blank" href="http://baike.baidu.com/view/179294.htm">软骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">；<img src="/biyesheji/plugins/kindeditor/attached/image/20160507/20160507000846_23837.jpg" alt="" width="268" height="161" title="" align="left" />体内无小骨乱刺，骨头呈白色玉石状，内脏团小、出肉率高，肌肉丰厚白嫩。漠斑牙鲆形态与中国褐牙鲆相似，体侧扁，卵圆形，两眼均位于头部左侧，身体的左侧呈浅褐色，分布有不规则的斑点，腹部颜色较浅，能随着周围环境而变化，以便隐藏身体，躲避敌害。多宝鱼近似圆形；有眼的一侧（背面）呈青褐色，有点状黑色素及少量皮棘，无眼的一侧（腹面）呈白色；体表上还有隐约可见的黑色和棕色花纹，肌肉丰厚白嫩</span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><img src="/biyesheji/plugins/kindeditor/attached/image/20160507/20160507000900_24982.jpg" alt="" width="200" height="150" title="" align="right" /><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">卵圆形的身材，它的两眼在头部的左侧，它的长相不但奇特，而且多宝鱼皮下和鳍边含有丰富的胶质，头部及尾鳍均较小，鳍条为</span><a target="_blank" href="http://baike.baidu.com/view/179294.htm">软骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">；体内无小骨乱刺，骨头呈白色玉石状，内脏团小、出肉率高，肌肉丰厚白嫩。漠斑牙鲆形态与中国褐牙鲆相似，体侧扁，卵圆形，两眼均位于头部左侧，身体的左侧呈浅褐色，分布有不规则的斑点，腹部颜色较浅，能随着周围环境而变化，以便隐藏身体，躲避敌害。多宝鱼近似圆形；有眼的一侧（背面）呈青褐色，有点状黑色素及少量皮棘，无眼的一侧（腹面）呈白色；体表上还有隐约可见的黑色和棕色花纹，肌肉丰厚白嫩</span><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">卵圆形的身材，它的两眼在头部的左侧，它的长相不但奇特，而且多宝鱼皮下和鳍边含有丰富的胶质，头部及尾鳍均较小，鳍条为</span><a target="_blank" href="http://baike.baidu.com/view/179294.htm">软骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">；<img src="http://localhost/biyesheji/plugins/kindeditor/attached/image/20160507/20160507000846_23837.jpg" alt="" width="268" height="161" title="" align="left" />体内无小骨乱刺，骨头呈白色玉石状</span></span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">内脏团小、出肉率高，肌肉丰厚白嫩。漠斑牙鲆形态与中国褐牙鲆相似，体侧扁，卵圆形，两眼均位于头部左侧，身体的左侧呈浅褐色，分布有不规则的斑点，腹部颜色较浅，能随着周围环境而变化，以便隐藏身体，躲避敌害。多宝鱼近似圆形；有眼的一侧（背面）呈青褐色，有点状黑色素及少量皮棘，无眼的一侧（腹面）呈白色；体表上还有隐约可见的黑色和棕色花纹，肌肉丰厚白嫩</span><br />\r\n</span> \r\n</p>', '是', '是', '是', 123, '2016-05-12'),
(3, '草鱼', 5, '<a target="_blank" href="http://baike.baidu.com/view/36743.htm">草鱼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">一般喜栖居于</span><a target="_blank" href="http://baike.baidu.com/view/39079.htm">江河</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、湖泊等</span><a target="_blank" href="http://baike.baidu.com/view/385183.htm">水域</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">的中、下层和近岸多水草区域。具有河湖</span><a target="_blank" href="http://baike.baidu.com/subview/30418/13615174.htm">洄游</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">的习性，性成熟的个体在江河、水</span> \r\n<div class="album-wrap" style="border:1px solid #E0E0E0;">\r\n	<img src="/biyesheji/plugins/kindeditor/attached/image/20160507/20160507002730_50962.jpg" alt="" width="220" height="165" title="" align="right" /><br />\r\n</div>\r\n<div class="albumBg">\r\n	<div class="albumBgFir" style="margin:0px auto;">\r\n	</div>\r\n	<div class="albumBgSec" style="margin:0px auto;">\r\n	</div>\r\n</div>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"> 库等流水中产卵，</span><a target="_blank" href="http://baike.baidu.com/view/732647.htm">产卵</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">后的亲鱼和幼鱼进入支流及通江湖泊中，通常在被水淹没的浅滩草地和泛水区域以及干支流附属水体（</span><a target="_blank" href="http://baike.baidu.com/view/9464.htm">湖泊</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、小河、港道等水草丛生地带）</span><a target="_blank" href="http://baike.baidu.com/view/1823908.htm">摄食</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">育肥。冬季则在干流或湖泊的深水处越冬。草鱼性情活泼，游泳迅速，常成群觅食，性贪食，为典型的草食性鱼类。其</span><a target="_blank" href="http://baike.baidu.com/view/1099218.htm">鱼苗</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">阶段摄食</span><a target="_blank" href="http://baike.baidu.com/view/444692.htm">浮游动物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，幼鱼期兼食昆虫、</span><a target="_blank" href="http://baike.baidu.com/view/21697.htm">蚯蚓</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、</span><a target="_blank" href="http://baike.baidu.com/view/159230.htm">藻类</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">和</span><a target="_blank" href="http://baike.baidu.com/view/46086.htm">浮萍</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">等，体长约达10厘米以上时，完全摄食水生</span><a target="_blank" href="http://baike.baidu.com/view/38563.htm">高等植物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，其中尤以</span><a target="_blank" href="http://baike.baidu.com/view/2272065.htm">禾本科植物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">为多。草鱼摄食的植物种类随着生活环境里食物基础的状况而有所变化。</span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><img src="/biyesheji/plugins/kindeditor/attached/image/20160507/20160507002743_28509.jpg" alt="" width="220" height="165" title="" align="left" /><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">库等流水中产卵，</span><a target="_blank" href="http://baike.baidu.com/view/732647.htm">产卵</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">后的亲鱼和幼鱼进入支流及通江湖泊中，通常在被水淹没的浅滩草地和泛水区域以及干支流附属水体（</span><a target="_blank" href="http://baike.baidu.com/view/9464.htm">湖泊</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、小河、港道等水草丛生地带）</span><a target="_blank" href="http://baike.baidu.com/view/1823908.htm">摄食</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">育肥。冬季则在干流或湖泊的深水处越冬。草鱼性情活泼，游泳迅速，常成群觅食，性贪食，为典型的草食性鱼类。其</span><a target="_blank" href="http://baike.baidu.com/view/1099218.htm">鱼苗</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">阶段摄食</span><a target="_blank" href="http://baike.baidu.com/view/444692.htm">浮游动物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，幼鱼期兼食昆虫、</span><a target="_blank" href="http://baike.baidu.com/view/21697.htm">蚯蚓</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、</span><a target="_blank" href="http://baike.baidu.com/view/159230.htm">藻类</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">和</span><a target="_blank" href="http://baike.baidu.com/view/46086.htm">浮萍</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">等，体长约达10厘米以上时，完全摄食水生</span><a target="_blank" href="http://baike.baidu.com/view/38563.htm">高等植物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，其中尤以</span><a target="_blank" href="http://baike.baidu.com/view/2272065.htm">禾本科植物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">为多。草鱼摄食的植物种类随着生活环境里食物基础的状况而有所变化。</span></span> \r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">库等流水中产卵，</span><a target="_blank" href="http://baike.baidu.com/view/732647.htm">产卵</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">后的亲鱼和幼鱼进入支流及通江湖泊中，通常在被水淹没的浅滩草地和泛水区域以及干支流附属水体（</span><a target="_blank" href="http://baike.baidu.com/view/9464.htm">湖泊</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、小河、港道等水草丛生地带）</span><a target="_blank" href="http://baike.baidu.com/view/1823908.htm">摄食</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">育肥。冬季则在干流或湖泊的深水处越冬。草鱼性情活泼，游泳迅速，常成群觅食，性贪食，为典型的草食性鱼类。其</span><a target="_blank" href="http://baike.baidu.com/view/1099218.htm">鱼苗</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">阶段摄食</span><a target="_blank" href="http://baike.baidu.com/view/444692.htm">浮游动物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">，幼鱼期兼食昆虫、</span><a target="_blank" href="http://baike.baidu.com/view/21697.htm">蚯蚓</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、</span><a target="_blank" href="http://baike.baidu.com/view/159230.htm">藻类</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">和</span><a target="_blank" href="http://baike.baidu.