--
-- 表的结构 `zhaopin`
--

CREATE TABLE IF NOT EXISTS `zhaopin` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(80) NOT NULL,
  `content` text NOT NULL,
  `status` varchar(50) DEFAULT '',
  `time` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=11 ;

--
-- 转存表中的数据 `zhaopin`
--

INSERT INTO `zhaopin` (`id`, `title`, `content`, `status`, `time`) VALUES
(2, '市场专员', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">一、岗位职责：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、根据人员需求进行招聘作业，在招聘周期内满足人员需求；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、带领新人开拓市场达成销售目标；&nbsp;</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">二、任职资格:</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、22岁—35岁，高中及以上学历 ，一年工作经验以上；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、能积极主动学习，有很强的自我约束和自我管理能力 ；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、有很强的企图心。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">三、工作时间:</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">周一至周五8:30~10:30，双休，按照国家法定节假日放假</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">详情请咨询许小姐：18927596329 电子邮箱：395602779@qq.com</span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">商务助理</span>\r\n	</p>\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">关键字：</span><span class="el">经理助理</span>&nbsp;<span class="el">财务</span>&nbsp;<span class="el">会计</span>&nbsp;<span class="el">客服</span>&nbsp;<span class="el">保险客户服务</span>&nbsp;<span class="el">业务助理</span>\r\n	</p>\r\n</div>', '', '2016-05-05'),
(3, '人力资源实习生', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">岗位职责：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、员工档案归档整理与管理；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、协助人力资源准备所需资料、整理报销单据；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、协助开展员工活动，帮助解决员工各类问题；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、领导交付的其他工作。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、熟悉人力资源各模块相关内容，人力资源管理专业优先考虑；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、熟练使用办公设备，熟练使用Word、Excel等office办公软件；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、做事认真、有较强的责任心和协调能力。</span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">人事助理</span>\r\n	</p>\r\n</div>', '', '2016-05-05'),
(4, '商品期货研究员', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="font-size:12pt;font-family:微软雅黑, sans-serif;color:#333333;line-height:30px;background:white;">岗位职责：</span><span style="background-color:#FFFFFF;"><br />\r\n</span>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">1</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">、<span style="font-size:14px;font-family:''Microsoft YaHei'';">&nbsp;</span>收集相关品种市场数据信息，建立和维护基础信息数据库；<span style="font-size:14px;font-family:''Microsoft YaHei'';"></span></span>\r\n</p>\r\n<span style="background-color:#FFFFFF;"><br />\r\n</span>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">2</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">、<span style="font-size:14px;font-family:''Microsoft YaHei'';">&nbsp;</span>负责相关品种的研究和分析，并撰写相关研究分析报告；<span style="font-size:14px;font-family:''Microsoft YaHei'';"></span></span>\r\n</p>\r\n<span style="background-color:#FFFFFF;"><br />\r\n</span>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">3</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">、深入产业链进行调研，对所研究品种的上下游进行详细了解。</span>\r\n</p>\r\n<span style="background-color:#FFFFFF;"><br />\r\n</span>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;"><span></span></span>\r\n</p>\r\n<span style="background-color:#FFFFFF;"><br />\r\n</span>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">岗位要求：</span>\r\n</p>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">1、在校硕士研究生，专业不限；</span>\r\n</p>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">2、</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">一周五工作日准时上班到岗，允许偶尔请假；</span>\r\n</p>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">3、</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">工作严谨细致，责任心强，性格沉稳，诚实守信；</span>\r\n</p>\r\n<p style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background:white;">\r\n	<span style="font-size:12pt;font-family:微软雅黑, sans-serif;">4</span><span style="font-size:12pt;font-family:微软雅黑, sans-serif;">、有较强的逻辑思维能力、数据处理能力，善于独立思考和分析判断。</span>\r\n</p>\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;"></span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">证券/期货/外汇经纪</span>\r\n	</p>\r\n</div>', '', '2016-05-05'),
(5, '期货研究员/分析师', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">【岗位职责】</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、负责现货、期货信息的收集整理、建立数据库；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、对期货行情进行跟踪研判，撰写报告，及时提示投资机会和风险；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、为产业客户提供全面期货咨询服务；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、定期在媒体刊物上发表文章和评论，提升公司品牌知名度。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">【任职要求】</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、著名高校硕士研究生及以上学历，金融、经济、数学、统计、化工等专业；&nbsp;</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、较强的写作与语言文字表达能力，较好的英文运用能力；&nbsp;</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、出色的独立思考和分析能力，有数理基础优先；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、对期货行业有一定的了解，有期货从业资格证、期货投资咨询资格证者优先。</span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">金融/经济研究员</span>\r\n	</p>\r\n</div>', '', '2016-05-05'),
(6, '销售代表', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、完成公司下达的销售任务；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、负责新客户的开发和老客户的维护；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、遵守公司规章制度，完善客户的档案，按时提交工作报告；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、按时参加公司召开的销售会议和产品培训；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">5、关注产品行业动态，及时向公司反馈市场信息。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">大专以上学历，男女不限，有无经验均可，热爱销售工作和较强的客户服务意识。</span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">销售代表</span>\r\n	</p>\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">关键字：</span><span class="el">直销销售</span>\r\n	</p>\r\n</div>', '', '2016-05-05'),
(7, '行政文员', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、大学在读；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、财会或法律相关专业优先；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、每周可实习2至3天，不要求全天。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">岗位职责：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、负责公司各类电脑文档的编号、打印、排版和归档；报表的收编以及整理；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、会用一般办公软件如excel，word，负责收发邮件，传真机复印机使用，以便更好的贯彻和落实工作；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、完成领导交办的其他行政、后勤工作</span>', '', '2016-05-05'),
(8, '外呼专员/电话销售', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">岗位职责：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、通过主动呼出，对目标客户进行简单咨询的沟通并邀约；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、详细记录客户信息，解答客户咨询，为客户提供优质服务；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、 学校提供资源 ，负责电话外呼即可 ，工作时间弹性制，工作时间周一至周五。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、有无销售经验均可，有电话销售或互联网销售经验优先 ，可接受大三大四学生假期实习；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、语言表达能力较强，有责任心，工作耐心细致，普通话标准即可</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、入职前提供相关培训，实习成绩优秀者可直接转正</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、详情可联系人事部王老师：0411-65092302 简历可直接投递或发至邮箱 dl_hr@xinghuo100.ne</span>', '', '2016-05-05'),
(9, '市场专员', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">一、专业要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">全日制统招大专以上学历，市场营销或相近专业</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">二、年龄要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">25周岁以上35周岁以下</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">三、经验要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、两年以上教育培训市场营销工作经验，有青少儿英语及艺术类教育培训机构市场营销工作经验者优先考虑；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、具备项目的独立策划和执行能力；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、具备较强的文案写作及表达能力；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、具备优异的市场推广及销售业绩，具备客户资源者优先考虑；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">5、良好的表达能力以及沟通协调能力，公关能力强；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">6、良好的团队协作精神，工作细致认真，责任心强，能够承担工作压力。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">四、沟通能力要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">普通话流利，能够准确清晰地与学生及学生家长进行沟通；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">五、其他要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">能够熟练操作OFFICE等办公软件。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">工作内容：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、协助学校课程整体营销推广计划（托福、SAT等）的执行；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、负责市场分析数据的收集工作；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、总结市场营销活动效果，为调整市场推广计划作好信息收集工作；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、协助市场部经理做好所计划的公开课、招生咨询会、大型活动等；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">5、维护合作伙伴和招生渠道的日常沟通关系；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">6、协助市场部经理做好新项目的开发、策划和执行；</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">7、完成市场部经理临时安排的工作</span>', '', '2016-05-05'),
(10, '电商运营/市场营销专员', '<span class="label" style="font-size:14px;font-family:''Microsoft YaHei'';color:#666666;line-height:30px;background-color:#FFFFFF;">职位描述：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、负责每日对商城陈列商品信息的核查，通过对店铺巡店过程中发现的问题，进行分析总结</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、负责对淘宝、京东等电商商品进行价格比对，了解市场动态和市场变化。并作出正确的应对方式。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、监督、指导终端店铺促销活动的执行，对促销结果进行分析，提高终端销售。</span><br />\r\n<br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">任职要求：</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">1、	具备较强的营销能力、有商品管理技能、熟练操作常规办公软件。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">2、	良好的心理素质，敬业的工作精神，能吃苦耐劳，具有良好的判断能力、交际能力、沟通能力、计划与执行能力。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">3、	熟悉了解淘宝等网络购物平台，并经常通过这些平台购物。对化妆品方面熟悉，具备优秀的审美能力，时尚意识。</span><br />\r\n<span style="color:#333333;font-family:''Microsoft YaHei'';font-size:14px;line-height:30px;background-color:#FFFFFF;">4、	招实习生，人生不是没有机会，而是缺乏眼光和把握机会的智慧。</span>\r\n<div class="mt10" style="font-size:14px;font-family:''Microsoft YaHei'';color:#333333;background-color:#FFFFFF;">\r\n	<p class="fp f2">\r\n		<span class="label" style="color:#666666;">职能类别：</span><span class="el">业务分析专员/助理</span>&nbsp;<span class="el">市场分析/调研人员</span>\r\n	</p>\r\n</div>', '', '2016-05-05');

